CREATE OR REPLACE PROCEDURE cnes_carregar_equipamento ( nr_seq_identificacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_equipamento_w		bigint;
nr_seq_grupo_w			bigint;

C01 CURSOR FOR
	SELECT	nr_sequencia
	from	cnes_equipamento	a
	where	not exists (SELECT b.nr_seq_equipamento from cnes_equipamento_ficha b where a.nr_sequencia = b.nr_seq_equipamento);


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_equipamento_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin
	select	max(nr_seq_grupo)
	into STRICT	nr_seq_grupo_w
	from	cnes_equipamento
	where	nr_sequencia	= nr_seq_equipamento_w;

	insert into cnes_equipamento_ficha(
		nr_sequencia, nr_seq_identificacao, nr_seq_equipamento,
		qt_existente, qt_em_uso, ie_sus,
		dt_atualizacao, nm_usuario, dt_atualizacao_nrec,
		nm_usuario_nrec, nr_seq_grupo)
	values (	nextval('cnes_equipamento_ficha_seq'), nr_seq_identificacao_p, nr_seq_equipamento_w,
		0, 0, 'N',
		clock_timestamp(), nm_usuario_p, clock_timestamp(),
		nm_usuario_p, nr_seq_grupo_w);
	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
