CREATE OR REPLACE PROCEDURE cns_atualizar_cod_domicilio ( cd_municipio_ibge_p text, nr_seq_cadastrador_p bigint, nr_ficha_p bigint, cd_domicilio_p INOUT text) AS $body$
DECLARE



cd_municipio_ibge_w	varchar(6);
cd_cadastrador_w	integer;
cd_domicilio_w		varchar(16)	:= '';


BEGIN

if (cd_municipio_ibge_p IS NOT NULL AND cd_municipio_ibge_p::text <> '') and (nr_seq_cadastrador_p IS NOT NULL AND nr_seq_cadastrador_p::text <> '') and (nr_ficha_p IS NOT NULL AND nr_ficha_p::text <> '') then

	select	coalesce(max(substr(cd_cadastrador,1,5)),'')
	into STRICT	cd_cadastrador_w
	from	cns_cadastrador
	where	nr_sequencia	= nr_seq_cadastrador_p;

	cd_municipio_ibge_w	:= substr(cd_municipio_ibge_p,1,6);
	cd_domicilio_w		:= cd_municipio_ibge_w||cd_cadastrador_w||nr_ficha_p;
	cd_domicilio_w		:= cd_domicilio_w||calcula_digito('MODULO11',cd_domicilio_w);
end if;

cd_domicilio_p	:= cd_domicilio_w;

END;
