CREATE OR REPLACE PROCEDURE cns_atualizar_dados_domicilio ( cd_pessoa_fisica_p text, cd_municipio_ibge_p INOUT text, cd_cep_p INOUT text, ds_endereco_p INOUT text, nr_endereco_p INOUT text, ds_bairro_p INOUT text) AS $body$
DECLARE



cd_municipio_ibge_w	varchar(6);
cd_cep_w		varchar(15);
ds_endereco_w		varchar(100);
nr_endereco_w		varchar(7);
ds_bairro_w		varchar(30);


BEGIN

/* OBTER OS DADOS DO PACIENTE*/

begin
select	b.cd_municipio_ibge,
	b.cd_cep,
	b.ds_endereco,
	b.nr_endereco,
	substr(b.ds_bairro,1,30)
into STRICT	cd_municipio_ibge_w,
	cd_cep_w,
	ds_endereco_w,
	nr_endereco_w,
	ds_bairro_w
from	compl_pessoa_fisica	b,
	pessoa_fisica		a
where	a.cd_pessoa_fisica	= b.cd_pessoa_fisica
and	b.ie_tipo_complemento	= 1
and	a.cd_pessoa_fisica	= cd_pessoa_fisica_p;
exception
	when others then
		cd_municipio_ibge_w	:= null;
		cd_cep_w		:= null;
		ds_endereco_w		:= null;
		nr_endereco_w		:= null;
		ds_bairro_w		:= null;
end;

cd_municipio_ibge_p	:= cd_municipio_ibge_w;
cd_cep_p		:= cd_cep_w;
ds_endereco_p		:= ds_endereco_w;
nr_endereco_p		:= nr_endereco_w;
ds_bairro_p		:= ds_bairro_w;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
