SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE cns_atualizar_usuario_arquivo ( nr_seq_controle_p bigint, nr_seq_usuario_p bigint, ie_operacao_p bigint, nm_usuario_p text) AS $body$
DECLARE


/* IE_OPERACAO_P
	1 - Inclusao
	2 - Exclusao
*/
nr_seq_domicilio_w	bigint;


BEGIN

select	coalesce(max(nr_sequencia),0)
into STRICT	nr_seq_domicilio_w
from	cns_domicilio_usuario
where	nr_seq_usuario	= nr_seq_usuario_p
and	ie_situacao	= 'A';

if (ie_operacao_p	= 1) and (nr_Seq_domicilio_w > 0) then
	update	cns_usuario
	set	dt_inclusao_usuario	= clock_timestamp(),
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_usuario_p;

	update	cns_domicilio_usuario
	set	nr_seq_controle_envio	= nr_Seq_controle_p,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_Seq_domicilio_w;
elsif (ie_operacao_p	= 2) then
	update	cns_usuario
	set	dt_inclusao_usuario	 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_sequencia		= nr_seq_usuario_p;

	update	cns_domicilio_usuario
	set	nr_seq_controle_envio	 = NULL,
		nm_usuario		= nm_usuario_p,
		dt_atualizacao		= clock_timestamp()
	where	nr_seq_controle_envio	= nr_seq_controle_p
	and	nr_Seq_usuario		= nr_seq_usuario_p;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
