CREATE OR REPLACE PROCEDURE compare_mims_material_value (CD_MATERIAL_P imp_material.CD_MATERIAL%type, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


isDirtyCheck smallint := 0;

BEGIN

-- check material_reacao ( Reactions )
select 1
into STRICT isDirtyCheck
from imp_material_reacao a, material_reacao b
where a.CD_MATERIAL = CD_MATERIAL_P
and a.CD_MATERIAL = b.CD_MATERIAL
and trim(both a.DS_REACAO) = trim(both b.DS_REACAO);

  if (isDirtyCheck = 1) then
    update imp_material_reacao a set a.IE_DIRTY_CHECK = 1 where a.CD_MATERIAL = CD_MATERIAL_P;
  end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
