CREATE OR REPLACE PROCEDURE compensar_cheque_cr_repasse (cd_estabelecimento_p bigint, qt_dias_uteis_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_interno_conta_w		bigint;
vl_saldo_titulo_w		double precision;
dt_deposito_w		timestamp;
nr_seq_cheque_w		bigint;

c01 CURSOR FOR
SELECT	e.nr_seq_cheque,
	b.nr_interno_conta,
	c.vl_saldo_titulo,
	trunc(coalesce(e.dt_seg_reapresentacao, coalesce(e.dt_reapresentacao, e.dt_deposito)),'dd')
from	convenio d,
	procedimento_paciente b,
	procedimento_repasse a,
	titulo_receber c,
	cheque_cr e
where	coalesce(a.nr_repasse_terceiro::text, '') = ''
and	a.nr_seq_procedimento	= b.nr_sequencia
and	coalesce(b.cd_motivo_exc_conta::text, '') = ''
and	b.nr_interno_conta		= c.nr_interno_conta
and	b.cd_convenio		= d.cd_convenio
and	d.ie_tipo_convenio		= 1
and	c.cd_estabelecimento	= cd_estabelecimento_p
and	c.nr_titulo			= e.nr_titulo
and	a.ie_status		in ('A','U','D')		/* jacson os 30239 - inclui 'D'*/
and	coalesce(e.dt_seg_reapresentacao, coalesce(e.dt_reapresentacao, e.dt_deposito)) is not null
and	coalesce(e.dt_devolucao::text, '') = ''
and	coalesce(e.dt_seg_devolucao::text, '') = ''

union

select	e.nr_seq_cheque,
	b.nr_interno_conta,
	c.vl_saldo_titulo,
	trunc(coalesce(e.dt_seg_reapresentacao, coalesce(e.dt_reapresentacao, e.dt_deposito)),'dd')
from    convenio d,
	material_atend_paciente b,
	material_repasse a,
	titulo_receber c,
	cheque_cr e
where	coalesce(a.nr_repasse_terceiro::text, '') = ''
and	a.nr_seq_material		= b.nr_sequencia
and	coalesce(b.cd_motivo_exc_conta::text, '') = ''
and	b.nr_interno_conta		= c.nr_interno_conta
and	b.cd_convenio		= d.cd_convenio
and	d.ie_tipo_convenio		= 1
and	c.cd_estabelecimento	= cd_estabelecimento_p
and	c.nr_titulo			= e.nr_titulo
and	a.ie_status		in ('A','U','D')		/* jacson os 30239 - inclui 'D'*/
and	coalesce(e.dt_seg_reapresentacao, coalesce(e.dt_reapresentacao, e.dt_deposito)) is not null
and	coalesce(e.dt_devolucao::text, '') = ''
and	coalesce(e.dt_seg_devolucao::text, '') = '';


BEGIN


open c01;
loop
fetch c01 into
	nr_seq_cheque_w,
	nr_interno_conta_w,
	vl_saldo_titulo_w,
	dt_deposito_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin
	if (obter_dias_uteis_periodo(dt_deposito_w, clock_timestamp(), cd_estabelecimento_p) >= qt_dias_uteis_p) then
		CALL atualizar_repasse_conta(nr_interno_conta_w, vl_saldo_titulo_w, nm_usuario_p);

		update cheque_cr
		set    dt_compensacao	= clock_timestamp()
		where  nr_seq_cheque	= nr_seq_cheque_w;
	end if;
	end;
end loop;
close c01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
