SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE compensar_cheque_cr ( cd_estabelecimento_p bigint, qt_dias_uteis_p bigint) AS $body$
DECLARE

 
ds_historico_w			cheque_cr_hist.ds_historico%type;
nr_seq_cheque_w			cheque_cr.nr_seq_cheque%type;

c01 CURSOR FOR 
SELECT	nr_seq_cheque 
from	cheque_cr 
where	cd_estabelecimento			= cd_estabelecimento_p 
and	obter_status_cheque(nr_seq_cheque)	in (2,4,9) 
and	OBTER_DIAS_UTEIS_PERIODO(trunc(coalesce(DT_SEG_REAPRESENTACAO, coalesce(DT_REAPRESENTACAO, DT_DEPOSITO)), 'dd'), trunc(clock_timestamp(), 'dd'), cd_estabelecimento_p) >= qt_dias_uteis_p 
and	coalesce(dt_compensacao::text, '') = '' 
and	coalesce(dt_devolucao::text, '') = '' 
and	coalesce(dt_venda_terc::text, '') = '' 
and	coalesce(dt_saque::text, '') = '' 
and	coalesce(ie_lib_caixa,'S')			= 'S';


BEGIN 
 
open c01;
loop 
fetch c01 into 
	nr_seq_cheque_w;
EXIT WHEN NOT FOUND; /* apply on c01 */
	begin 
	update cheque_cr	 
	set  dt_compensacao	= clock_timestamp() 
	where nr_seq_cheque	= nr_seq_cheque_w;
	/*'Compensado pelo job COMPENSAR_CHEQUE_CR'*/
 
	ds_historico_w	:= substr(wheb_mensagem_Pck.get_texto(302637),1,4000);
	 
	insert into cheque_cr_hist( 
		NR_SEQUENCIA, 
		DT_ATUALIZACAO, 
		NM_USUARIO, 
		DT_ATUALIZACAO_NREC, 
		NM_USUARIO_NREC, 
		NR_SEQ_CHEQUE, 
		DS_HISTORICO) 
	values (	nextval('cheque_cr_hist_seq'), 
		clock_timestamp(), 
		'JobTasy', 
		clock_timestamp(), 
		'JobTasy', 
		nr_seq_cheque_w, 
		ds_historico_w);
	end;
end loop;
close c01;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE compensar_cheque_cr ( cd_estabelecimento_p bigint, qt_dias_uteis_p bigint) FROM PUBLIC;

