CREATE OR REPLACE PROCEDURE compila_objetos_invalidos () AS $body$
DECLARE


nm_objeto_w		varchar(100)	:= null;
ie_tipo_objeto_w       	varchar(32)	:= null;
qt_processo_w		bigint	:= 0;
ds_comando_w		varchar(512)	:= null;
ds_erro_w		varchar(4000);
c001			integer;
c010			integer;
retorno_w			bigint;
ds_comando_2_w		varchar(1000);
qt_nivel_w		smallint		:= 0;


BEGIN

CALL gravar_processo_longo('','VALIDA_OBJETOS_SISTEMA',qt_processo_w);

qt_nivel_w	:= coalesce(Obter_Valor_Param_Usuario(0,167, Obter_perfil_Ativo, wheb_usuario_pck.get_nm_usuario, 0),0);

if (qt_nivel_w = 1) then
	CALL exec_sql_dinamico('TASY', 'BEGIN SYS.UTL_RECOMP.recomp_serial('|| chr(39) || USER || chr(39) ||'); END;');
elsif (qt_nivel_w > 1) then
	CALL exec_sql_dinamico('TASY', 'BEGIN SYS.UTL_RECOMP.recomp_parallel(' || qt_nivel_w || ',' || chr(39) || USER || chr(39) || '); END;');
else
	ds_comando_2_w :=	' select	Object_name, object_type' 	||
				' from	user_objects ' 		||
				' where	object_type in (''PROCEDURE'', ''VIEW'', ''TRIGGER'', ''PACKAGE'',''PACKAGE BODY'', ''FUNCTION'',''TYPE'')'||
				' and	status =  ''INVALID'''		||
				' order by	obter_ordem_objeto(object_type), object_name';

	C010 := DBMS_SQL.OPEN_CURSOR;
	DBMS_SQL.PARSE(C010, ds_comando_2_w, dbms_sql.Native);
	DBMS_SQL.DEFINE_COLUMN(C010, 1, nm_objeto_w,32);
	DBMS_SQL.DEFINE_COLUMN(C010, 2, ie_tipo_objeto_w,100);
	retorno_w := DBMS_SQL.execute(c010);

	while( DBMS_SQL.FETCH_ROWS(C010) > 0 ) loop
		begin
			DBMS_SQL.COLUMN_VALUE(C010, 1, nm_objeto_w );
			DBMS_SQL.COLUMN_VALUE(C010, 2, ie_tipo_objeto_w );

			if (ie_tipo_objeto_w = 'PACKAGE BODY') then
				ie_tipo_objeto_w := 'PACKAGE ';
				ds_comando_w := 'ALTER '||ie_tipo_objeto_w||' '||nm_objeto_w|| ' COMPILE BODY';
			else
				ds_comando_w := 'ALTER '||ie_tipo_objeto_w||' '||nm_objeto_w|| ' COMPILE';
			end if;
			qt_processo_w := qt_processo_w + 1;
			CALL gravar_processo_longo(ds_comando_w,'VALIDA_OBJETOS_SISTEMA',qt_processo_w);
	 		C001 := DBMS_SQL.OPEN_CURSOR;
			DBMS_SQL.PARSE(C001, ds_comando_w, dbms_sql.v7);
			DBMS_SQL.CLOSE_CURSOR(C001);
			if (ie_tipo_objeto_w = 'PACKAGE ') then
				C001 := DBMS_SQL.OPEN_CURSOR;
				DBMS_SQL.PARSE(C001, 'DBMS_SESSION.MODIFY_PACKAGE_STATE(DBMS_SESSION.REINITIALIZE)', dbms_sql.v7);
				DBMS_SQL.CLOSE_CURSOR(C001);
			end if;
		EXCEPTION
			WHEN OTHERS THEN
				DBMS_SQL.CLOSE_CURSOR(C001);
				ds_erro_w := SQLERRM(SQLSTATE);
				if (	SQLSTATE = -4020) then
				begin
					CALL abortar_se_base_wheb();
				exception
				when others then
					CALL wheb_mensagem_pck.exibir_mensagem_abort(282075,'NM_OBJETO='||nm_objeto_w ||';DS_ERRO='||ds_erro_w);
				end;
				end if;
			END;
	end loop;

	begin
		DBMS_SQL.CLOSE_CURSOR(C010);
	exception
	when others then
		DBMS_SQL.CLOSE_CURSOR(C010);
	end;
end	if;

END;
$body$
