SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE component_update_qt_max_row ( nr_sequencia_p bigint, ds_valores_max_row_p text ) AS $body$
DECLARE

    qt_registro_w   bigint;

BEGIN

	IF (coalesce(ds_valores_max_row_p::text, '') = '' OR ds_valores_max_row_p = '') then
		DELETE FROM
		configuracao_componente
		WHERE id_componente = nr_sequencia_p
		AND ie_tipo_componente = 'WCP'
		AND ie_tipo_configuracao = 'QT_MAX_REG';
	ELSE
		SELECT COUNT(ds_valor)
			INTO STRICT qt_registro_w
		FROM
		configuracao_componente
		WHERE id_componente = nr_sequencia_p
		AND ie_tipo_componente = 'WCP'
		AND ie_tipo_configuracao = 'QT_MAX_REG';

		IF (qt_registro_w = 0) THEN
			INSERT INTO configuracao_componente(
				id_componente,
				ie_tipo_componente,
				ie_tipo_configuracao,
				ds_valor
			) VALUES (
				nr_sequencia_p,
				'WCP',
				'QT_MAX_REG',
				ds_valores_max_row_p
			);
		ELSE
			UPDATE configuracao_componente
				SET ds_valor = ds_valores_max_row_p
			WHERE id_componente = nr_sequencia_p
			AND ie_tipo_componente = 'WCP'
			AND ie_tipo_configuracao = 'QT_MAX_REG';
		END IF;

	END IF;

    COMMIT;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
