CREATE OR REPLACE PROCEDURE comunicar_avaliador_produto ( cd_pessoa_avaliadora_p text, nm_usuario_p text, ds_titulo_p text, ds_mensagem_p text) AS $body$
DECLARE


nm_usuario_destino_w		varchar(15);
nr_seq_classif_w		bigint;

BEGIN

select	obter_usuario_pessoa(cd_pessoa_avaliadora_p)
into STRICT	nm_usuario_destino_w
;

if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then

	select	obter_classif_comunic('F')
	into STRICT	nr_seq_classif_w
	;

	CALL Gerar_Comunic_Padrao(	clock_timestamp(),
				substr(ds_titulo_p,1,255),
				ds_mensagem_p,
				nm_usuario_p,
				'N',
				nm_usuario_destino_w,
				'N',
				nr_seq_classif_w,
				'',
				null,
				'',
				clock_timestamp(),
				'',
				'');
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
