SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE comunicar_medico_recado_agenda (nr_seq_recado_p bigint, nm_usuario_p text) AS $body$
DECLARE





ds_recado_w		varchar(2000);
nr_sequencia_w		bigint;
cd_medico_w		varchar(10);
nm_usuario_destino_w	varchar(15);


BEGIN

select	substr(ds_recado,1,2000),
	cd_medico
into STRICT	ds_recado_w,
	cd_medico_w
from	recado_medico
where	nr_sequencia	= nr_seq_recado_p;

nm_usuario_destino_w	:= obter_usuario_pf(cd_medico_w);

if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then

	select	nextval('comunic_interna_seq')
	into STRICT	nr_sequencia_w
	;


	insert into comunic_interna(
		dt_comunicado,
		ds_titulo,
		ds_comunicado,
		nm_usuario,
		dt_atualizacao,
		ie_geral,
		nm_usuario_destino,
		nr_sequencia,
		ie_gerencial,
		dt_liberacao,
		ds_perfil_adicional,
		ds_setor_adicional)
	values (	clock_timestamp(),
		obter_desc_expressao(774960),
		ds_recado_w,
		nm_usuario_p,
		clock_timestamp(),
		'N',
		nm_usuario_destino_w,
		nr_sequencia_w,
		'N',
		clock_timestamp(),
		null,
		null);

	commit;

end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
