SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE comunicar_usuario_parecer ( nr_sequencia_p bigint, nm_usuario_destino_p text, nm_usuario_p text) AS $body$
DECLARE





ds_historico_w		cot_compra_hist.ds_historico%type;
ds_titulo_w		cot_compra_hist.ds_titulo%type;
nr_seq_classif_w	comunic_interna.nr_seq_classif%type;
nr_sequencia_w		comunic_interna.nr_sequencia%type;
nr_cot_compra_w		cot_compra.nr_cot_compra%type;
ds_comunicado_w		cot_compra_hist.ds_historico%type;


BEGIN




select	ds_historico,
	ds_titulo,
	nr_cot_compra
into STRICT	ds_historico_w,
	ds_titulo_w,
	nr_cot_compra_w
from	cot_compra_hist
where	nr_sequencia	= nr_sequencia_p;

ds_titulo_w := substr(ds_titulo_w || ' - ' || Wheb_mensagem_pck.get_Texto(296308, 'NR_COT_COMPRA_W='||nr_cot_compra_w),1,255);

update	cot_compra_hist
set	ds_usuarios_envio_comunic = nm_usuario_destino_p,
	ie_evento = 'E'
where	nr_sequencia = nr_sequencia_p;

select	obter_classif_comunic('F')
into STRICT	nr_seq_classif_w
;

select	nextval('comunic_interna_seq')
into STRICT	nr_sequencia_w
;


insert into comunic_interna(
	dt_comunicado,
	ds_titulo,
	ds_comunicado,
	nm_usuario,
	dt_atualizacao,
	ie_geral,
	nm_usuario_destino,
	nr_sequencia,
	ie_gerencial,
	nr_seq_classif,
	dt_liberacao,
	ds_perfil_adicional,
	ds_setor_adicional)
values (	clock_timestamp(),
	ds_titulo_w,
	ds_historico_w,
	nm_usuario_p,
	clock_timestamp(),
	'N',
	nm_usuario_destino_p,
	nr_sequencia_w,
	'N',
	nr_seq_classif_w,
	clock_timestamp(),
	null,
	null);

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
