SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE comunica_excluir_item_aprov ( nr_documento_p bigint, nr_item_documento_p bigint, cd_material_p bigint, ie_tipo_documento_p text, nm_usuario_p text, ds_justificativa_p text) AS $body$
DECLARE


qt_existe_w			bigint;
ds_material_w			varchar(255);
qt_material_w			double precision;
cd_solicitante_w			varchar(10);
cd_setor_atendimento_w		integer;
cd_requisitante_w			varchar(10);
nr_seq_evento_w			bigint;
ds_titulo_w			varchar(255);
ds_comunicacao_w		varchar(4000);
nm_usuarios_adic_w		varchar(2000);
cd_perfil_w			integer;
ie_ci_lida_w			varchar(1);
ds_setor_enviar_w			varchar(2000);
ds_perfil_enviar_w			varchar(2000);
cd_pessoa_fisica_w		varchar(10);
nm_usuario_w			varchar(15);
ds_lista_usuario_w			varchar(2000);
ds_documento_w			varchar(50);
nr_seq_classif_w			bigint;
nr_seq_comunic_w			bigint;
cd_estabelecimento_w		integer;

c01 CURSOR FOR
SELECT	b.nr_sequencia,
	b.ds_titulo,
	b.ds_comunicacao,
	b.nm_usuarios_adic,
	b.cd_perfil,
	b.ie_ci_lida
from	regra_envio_comunic_compra a,
	regra_envio_comunic_evento b
where	a.nr_sequencia = b.nr_seq_regra
and	a.cd_funcao = 267
and	b.cd_evento = 69
and	cd_estabelecimento = cd_estabelecimento_w
and	ie_situacao = 'A'
and	((coalesce(b.cd_setor_destino::text, '') = '') or (coalesce(cd_setor_atendimento_w,0) = coalesce(b.cd_setor_destino,0)))
and	substr(obter_se_envia_ci_regra_compra(b.nr_sequencia,nr_documento_p,ie_tipo_documento_p,obter_perfil_ativo,nm_usuario_p,null),1,1) = 'S';

c02 CURSOR FOR
SELECT	cd_setor_atendimento
from	regra_envio_comunic_usu
where	nr_seq_evento = nr_seq_evento_w
and	(cd_setor_atendimento IS NOT NULL AND cd_setor_atendimento::text <> '')
group by	cd_setor_atendimento;

c03 CURSOR FOR
SELECT	cd_perfil
from	regra_envio_comunic_usu
where	nr_seq_evento = nr_seq_evento_w
and	(cd_perfil IS NOT NULL AND cd_perfil::text <> '')
group by	cd_perfil;

c04 CURSOR FOR
SELECT	CASE WHEN ie_usuario='SPR' THEN cd_solicitante_w END
from	regra_envio_comunic_usu
where	nr_seq_evento = nr_seq_evento_w
and	(ie_usuario IS NOT NULL AND ie_usuario::text <> '')
group by	ie_usuario;


BEGIN

select	count(*)
into STRICT	qt_existe_w
from	regra_envio_comunic_compra a,
	regra_envio_comunic_evento b
where	a.nr_sequencia = b.nr_seq_regra
and	a.cd_funcao = 267
and	b.cd_evento = 69
and	ie_situacao = 'A'
and	exists (SELECT	1
		from	regra_envio_comunic_usu x
		where	x.nr_seq_evento = b.nr_sequencia);

if (qt_existe_w > 0) then
	begin

	if (ie_tipo_documento_p = 'OC') then
		begin

		select	c.ds_material,
			b.qt_material,
			a.cd_pessoa_solicitante,
			coalesce(a.cd_setor_atendimento,0),
			a.cd_estabelecimento
		into STRICT	ds_material_w,
			qt_material_w,
			cd_solicitante_w,
			cd_setor_atendimento_w,
			cd_estabelecimento_w
		from	material c,
			ordem_compra_item b,
			ordem_compra a
		where	c.cd_material = b.cd_material
		and	b.nr_ordem_compra = a.nr_ordem_compra
		and	b.nr_item_oci = nr_item_documento_p
		and	a.nr_ordem_compra = nr_documento_p;

		end;
	elsif (ie_tipo_documento_p = 'SC') then
		begin

		select	c.ds_material,
			b.qt_material,
			a.cd_pessoa_solicitante,
			coalesce(a.cd_setor_atendimento,0),
			a.cd_estabelecimento
		into STRICT	ds_material_w,
			qt_material_w,
			cd_solicitante_w,
			cd_setor_atendimento_w,
			cd_estabelecimento_w
		from	material c,
			solic_compra_item b,
			solic_compra a
		where	c.cd_material = b.cd_material
		and	b.nr_solic_compra = a.nr_solic_compra
		and	b.nr_item_solic_compra = nr_item_documento_p
		and	a.nr_solic_compra = nr_documento_p;

		end;
	elsif (ie_tipo_documento_p = 'RM') then
		begin

		select	c.ds_material,
			b.qt_material_requisitada,
			coalesce(a.cd_pessoa_solicitante,0),
			a.cd_pessoa_requisitante,
			coalesce(a.cd_setor_atendimento,0),
			a.cd_estabelecimento
		into STRICT	ds_material_w,
			qt_material_w,
			cd_solicitante_w,
			cd_requisitante_w,
			cd_setor_atendimento_w,
			cd_estabelecimento_w
		from	material c,
			item_requisicao_material b,
			requisicao_material a
		where	c.cd_material = b.cd_material
		and	b.nr_requisicao = a.nr_requisicao
		and	b.nr_sequencia = nr_item_documento_p
		and	a.nr_requisicao = nr_documento_p;

		select	count(*)
		into STRICT	qt_existe_w
		from	usuario
		where	cd_pessoa_fisica = cd_solicitante_w;

		if (qt_existe_w = 0) then
			cd_solicitante_w	:= cd_requisitante_w;
		end if;

		end;
    elsif (ie_tipo_documento_p = 'CC') then
		begin

        select  c.ds_material,
            b.qt_material,
            a.cd_pessoa_solicitante,
            a.cd_estabelecimento
        into STRICT    ds_material_w,
            qt_material_w,
            cd_solicitante_w,
            cd_estabelecimento_w
        from    material c,
            cot_compra_item b,
            cot_compra a
        where   c.cd_material = b.cd_material
        and	    b.nr_cot_compra = a.nr_cot_compra
        and	a.nr_cot_compra	        = nr_item_documento_p
        and	b.nr_item_cot_compra	= nr_documento_p;

		end;
	end if;

	open c01;
	loop
	fetch c01 into
		nr_seq_evento_w,
		ds_titulo_w,
		ds_comunicacao_w,
		nm_usuarios_adic_w,
		cd_perfil_w,
		ie_ci_lida_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
		begin

		open c02;
		loop
		fetch c02 into
			cd_setor_atendimento_w;
		EXIT WHEN NOT FOUND; /* apply on c02 */
			begin

			ds_setor_enviar_w	:= ds_setor_enviar_w || to_char(cd_setor_atendimento_w) || ', ';

			end;
		end loop;
		close c02;

		if (cd_perfil_w IS NOT NULL AND cd_perfil_w::text <> '') then
			ds_perfil_enviar_w	:= to_char(cd_perfil_w) || ', ';
		end if;

		open c03;
		loop
		fetch c03 into
			cd_perfil_w;
		EXIT WHEN NOT FOUND; /* apply on c03 */
			begin

			ds_perfil_enviar_w	:= ds_perfil_enviar_w || to_char(cd_perfil_w) || ', ';

			end;
		end loop;
		close c03;

		open c04;
		loop
		fetch c04 into
			cd_pessoa_fisica_w;
		EXIT WHEN NOT FOUND; /* apply on c04 */
			begin

			select	coalesce(max(nm_usuario),'X')
			into STRICT	nm_usuario_w
			from	usuario
			where	cd_pessoa_fisica = cd_pessoa_fisica_w;

			if (nm_usuario_w <> 'X') then
				ds_lista_usuario_w	:= ds_lista_usuario_w || nm_usuario_w || ', ';
			end if;

			end;
		EXIT WHEN NOT FOUND; /* apply on c04 */
		end loop;
		close c04;

		if (ds_lista_usuario_w IS NOT NULL AND ds_lista_usuario_w::text <> '') then
			ds_lista_usuario_w	:=	ds_lista_usuario_w || nm_usuarios_adic_w;
		end if;

		select	CASE WHEN ie_tipo_documento_p='SC' THEN Wheb_mensagem_pck.get_Texto(298460) WHEN ie_tipo_documento_p='OC' THEN Wheb_mensagem_pck.get_Texto(298461)  ELSE Wheb_mensagem_pck.get_Texto(298462) END
		into STRICT	ds_documento_w
		;

		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@ds_material',ds_material_w),1,255);
		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@cd_material',cd_material_p),1,255);
		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@nr_documento',nr_documento_p),1,255);
		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@nr_item',nr_item_documento_p),1,255);
		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@ds_tipo_documento',ds_documento_w),1,255);
		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@nm_usuario_exc',nm_usuario_p),1,255);
		ds_titulo_w		:= substr(replace_macro(ds_titulo_w,'@dt_exclusao',to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss')),1,255);

		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@ds_material',ds_material_w),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@cd_material',cd_material_p),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@nr_documento',nr_documento_p),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@nr_item',nr_item_documento_p),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@ds_tipo_documento',ds_documento_w),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@nm_usuario_exc',nm_usuario_p),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@dt_exclusao',to_char(clock_timestamp(),'dd/mm/yyyy hh24:mi:ss')),1,4000);
		ds_comunicacao_w	:= substr(replace_macro(ds_comunicacao_w,'@justificativa',substr(coalesce(ds_justificativa_p,''),1,2000)),1,4000);

		if (ds_lista_usuario_w IS NOT NULL AND ds_lista_usuario_w::text <> '') then
			begin

			select	obter_classif_comunic('F')
			into STRICT	nr_seq_classif_w
			;

			select	nextval('comunic_interna_seq')
			into STRICT	nr_seq_comunic_w
			;

			insert into comunic_interna(
				dt_comunicado,
				ds_titulo,
				ds_comunicado,
				nm_usuario,
				dt_atualizacao,
				ie_geral,
				nm_usuario_destino,
				nr_sequencia,
				ie_gerencial,
				nr_seq_classif,
				dt_liberacao,
				ds_perfil_adicional,
				ds_setor_adicional)
			values (	clock_timestamp(),
				ds_titulo_w,
				ds_comunicacao_w,
				nm_usuario_p,
				clock_timestamp(),
				'N',
				ds_lista_usuario_w,
				nr_seq_comunic_w,
				'N',
				nr_seq_classif_w,
				clock_timestamp(),
				ds_perfil_enviar_w,
				ds_setor_enviar_w);

			if (ie_ci_lida_w = 'S') then
				insert into comunic_interna_lida(nr_sequencia,nm_usuario,dt_atualizacao)values (nr_seq_comunic_w,nm_usuario_p,clock_timestamp());
			end if;

			end;
		end if;

		end;
	end loop;
	close c01;

	end;
end if;

/* Não deve ter commit porque se houver erro no processo de exclusão, não salva a CI */

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE comunica_excluir_item_aprov ( nr_documento_p bigint, nr_item_documento_p bigint, cd_material_p bigint, ie_tipo_documento_p text, nm_usuario_p text, ds_justificativa_p text) FROM PUBLIC;

