CREATE OR REPLACE PROCEDURE com_atualizar_status_sd_js ( ie_status_p text, nr_ordem_servico_p bigint, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p text, ie_commit_p text) AS $body$
