		dt_atualizacao 	= clock_timestamp(),
		nm_usuario 	= nm_usuario_p
	where	nr_ordem_servico = nr_ordem_servico_p;


elsif (ie_status_p = 'R') then

	update	com_solic_sd
	set	ie_status 		= ie_status_p,
		dt_atualizacao	 	= clock_timestamp(),
		nm_usuario	 	= nm_usuario_p
	where	nr_ordem_servico	= nr_ordem_servico_p;

	update	agenda_tasy
	set	ie_status	= 'E',
		dt_atualizacao 	= clock_timestamp(),
		nm_usuario 	= nm_usuario_p
	where	nr_ordem_servico = nr_ordem_servico_p;

elsif (ie_status_p = 'AG') then

	update	com_solic_sd
	set	ie_status 		= ie_status_p,
		dt_atualizacao	 	= clock_timestamp(),
		nm_usuario	 	= nm_usuario_p
	where	nr_ordem_servico	= nr_ordem_servico_p;

end if;

if (ie_commit_p = 'S') then
