CREATE OR REPLACE PROCEDURE com_atualiza_dt_solic_contr ( nr_sequencia_p com_solic_contrato.nr_sequencia%type, nm_usuario_p usuario.nm_usuario%type, ie_opcao_p text) AS $body$
BEGIN
if (coalesce(ie_opcao_p,'X') = 'A')then
	begin
	update	com_solic_contrato
	set	dt_aprov_reprov = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
	end;
elsif (coalesce(ie_opcao_p,'X') = 'V') then
	begin
	update	com_solic_contrato
	set	dt_avaliacao = clock_timestamp(),
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where nr_sequencia = nr_sequencia_p;
	end;
elsif (coalesce(ie_opcao_p,'X') = 'P') then
	begin
	update	com_solic_contrato
	set	dt_avaliacao  = NULL,
		dt_aprov_reprov  = NULL,
		nm_usuario = nm_usuario_p,
		dt_atualizacao = clock_timestamp()
	where	nr_sequencia = nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
