SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE com_cancelar_sd_js ( nr_seq_solicitacao_p bigint, nr_seq_motivo_p bigint, ds_justificativa_p text, nm_usuario_p text) AS $body$
DECLARE

 
nr_seq_ordem_w	bigint;				
				 

BEGIN 
 
if (coalesce(nr_seq_solicitacao_p,0) > 0) then 
	 
	 
		 
	select	coalesce(max(nr_ordem_servico),0) 
	into STRICT 	nr_seq_ordem_w 
	from	com_solic_sd 
	where	nr_sequencia = nr_seq_solicitacao_p;
		 
	if (nr_seq_ordem_w > 0) then 
	 
		CALL com_atualizar_status_sd_js('C', nr_seq_ordem_w, nr_seq_motivo_p, ds_justificativa_p, nm_usuario_p, 'S');
		 
		CALL man_encerrar_OS(nr_seq_ordem_w, 9, nm_usuario_p, '', 'S');
	else 
		update	com_solic_sd 
		set	ie_status 		= 'C', 
			dt_cancelamento 	= clock_timestamp(), 
			nm_usuario_cancel 	= nm_usuario_p, 
			nr_seq_motivo_cancel	= nr_seq_motivo_p, 
			ds_justif_cancel 	= ds_justificativa_p 
		where	nr_sequencia = nr_seq_solicitacao_p;
	 
	end if;
		 
end if;
 
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
