CREATE OR REPLACE PROCEDURE com_estornar_aprov_lead ( nr_sequencia_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
cd_empresa_w		smallint;
cd_cnpj_w		varchar(14);
ie_status_lead_w		varchar(03);


BEGIN 
 
select	ie_status_lead, 
	cd_cnpj 
into STRICT	ie_status_lead_w, 
	cd_cnpj_w 
from	com_solic_lead 
where	nr_sequencia = nr_sequencia_p;
 
if (ie_status_lead_w = 'A') then 
	begin 
	select	obter_empresa_estab(cd_estabelecimento_p) 
	into STRICT	cd_empresa_w 
	;
	 
	update	com_solic_lead 
	set	dt_aprov_reprov  = NULL, 
		nm_usuario	= nm_usuario_p, 
		ie_status_lead	= 'P', 
		dt_atualizacao	= clock_timestamp() 
	where	nr_sequencia	= nr_sequencia_p;
	 
	delete from pessoa_juridica_compl 
	where	cd_cgc 		= cd_cnpj_w;
	 
	delete from pessoa_juridica_estab 
	where	cd_cgc 		= cd_cnpj_w 
	and	cd_estabelecimento = cd_estabelecimento_p;
	 
	delete from com_cliente 
	where	cd_cnpj 		= cd_cnpj_w 
	and	cd_empresa 	= cd_empresa_w;
	 
	delete from pessoa_juridica 
	where	cd_cgc		= cd_cnpj_w;
	end;
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
