CREATE OR REPLACE PROCEDURE com_gerar_estim_prop ( nr_seq_proposta_p bigint, nr_seq_estimativa_p bigint, nr_seq_item_p bigint, nm_usuario_p text, cd_estabelecimento_p bigint) AS $body$
DECLARE


nr_seq_apres_w			bigint;
nr_seq_mod_impl_w		bigint;
ds_complemento_w			varchar(120);
qt_hora_w			bigint;
qt_reg_w				integer;
nr_seq_fase_w			smallint;
ds_observacao_w		varchar(255);

c01 CURSOR FOR
SELECT	a.nr_seq_apres,
	a.nr_seq_mod_implant,
	a.ds_complemento,
	a.qt_hora,
	a.nr_seq_fase,
	a.ds_observacao
from	com_estimativa_item a
where	a.nr_seq_estimativa	= nr_seq_estimativa_p
and	a.nr_sequencia		= coalesce(nr_seq_item_p, a.nr_sequencia);


BEGIN

select	count(*)
into STRICT	qt_reg_w
from	com_estimativa_item a
where	a.nr_seq_estimativa	= nr_seq_estimativa_p;

if (qt_reg_w = 0) then
	CALL Wheb_mensagem_pck.exibir_mensagem_abort(240516);
end if;

open c01;
loop
fetch c01 into
	nr_seq_apres_w,
	nr_seq_mod_impl_w,
	ds_complemento_w,
	qt_hora_w,
	nr_seq_fase_w,
	ds_observacao_w;
EXIT WHEN NOT FOUND; /* apply on c01 */


	insert into com_cliente_prop_estim(
		nr_sequencia,
		nr_seq_proposta,
		dt_atualizacao,
		nm_usuario,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		nr_seq_apres,
		nr_seq_mod_impl,
		ds_complemento,
		qt_hora,
		nr_seq_fase,
		nr_seq_estimativa,
		ds_observacao)
	values (	nextval('com_cliente_prop_estim_seq'),
		nr_seq_proposta_p,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_apres_w,
		nr_seq_mod_impl_w,
		ds_complemento_w,
		qt_hora_w,
		nr_seq_fase_w,
		nr_seq_estimativa_p,
		ds_observacao_w);
end loop;

commit;

end;
$body$
