CREATE OR REPLACE PROCEDURE com_gerar_modulo_estim_prop ( nr_seq_proposta_p bigint, nr_seq_estimativa_p bigint, ds_lista_modulos_p text, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_modulos_w	varchar(2000);
nr_pos_virgula_w	bigint;
nr_seq_modulo_w		bigint;


BEGIN
if (nr_seq_proposta_p IS NOT NULL AND nr_seq_proposta_p::text <> '') and (nr_seq_estimativa_p IS NOT NULL AND nr_seq_estimativa_p::text <> '') and (ds_lista_modulos_p IS NOT NULL AND ds_lista_modulos_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin
	ds_lista_modulos_w	:= ds_lista_modulos_p;
	while(ds_lista_modulos_w IS NOT NULL AND ds_lista_modulos_w::text <> '') loop
		begin
		nr_pos_virgula_w	:= position(',' in ds_lista_modulos_w);
		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_modulo_w		:= substr(ds_lista_modulos_w,0,nr_pos_virgula_w-1);
			ds_lista_modulos_w	:= substr(ds_lista_modulos_w,nr_pos_virgula_w+1,length(ds_lista_modulos_w));
			end;
		else
			begin
			nr_seq_modulo_w		:= (ds_lista_modulos_w)::numeric;
			ds_lista_modulos_w	:= null;
			end;
		end if;

		if (coalesce(nr_seq_modulo_w,0) > 0) then
			begin
			CALL com_gerar_estim_prop(nr_seq_proposta_p,
				nr_seq_estimativa_p,
				nr_seq_modulo_w,
				nm_usuario_p,
				cd_estabelecimento_p);
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
