SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE com_gravar_modulo_sd_js ( ds_lista_modulos_p text, nr_seq_solic_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_modulo_w		bigint;
tam_lista_w		bigint;
ie_pos_virgula_w	smallint := 0;
ds_lista_modulos_w	varchar(800);
qt_tempo_preparo_w      bigint;
qt_tempo_execucao_w     bigint;
nr_seq_tipo_abordagem_w bigint;


BEGIN

ds_lista_modulos_w := substr(ds_lista_modulos_p,1,800);

if (coalesce(nr_seq_solic_p,0) > 0) and (ds_lista_modulos_w IS NOT NULL AND ds_lista_modulos_w::text <> '') then

	select	max(nr_seq_tipo_abordagem)
	into STRICT	nr_seq_tipo_abordagem_w
	from	com_solic_sd
	where	nr_sequencia = nr_seq_solic_p;

	WHILE(ds_lista_modulos_w IS NOT NULL AND ds_lista_modulos_w::text <> '') LOOP
		begin

		tam_lista_w			:=	length(ds_lista_modulos_w);
		ie_pos_virgula_w		:=	position(',' in ds_lista_modulos_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_modulo_w		:= substr(ds_lista_modulos_w,1,(ie_pos_virgula_w - 1));
			ds_lista_modulos_w	:= substr(ds_lista_modulos_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		select	coalesce(max(qt_tempo_preparo),0),
			coalesce(max(qt_tempo_execucao),0)
		into STRICT	qt_tempo_preparo_w,
			qt_tempo_execucao_w
		from	com_tempo_demonstracao
		where	nr_seq_modulo = nr_seq_modulo_w;

		insert into com_solic_sd_mod(nr_sequencia,
						nr_seq_mod_impl,
						nr_seq_solic_sd,
						nm_usuario,
						dt_atualizacao,
						dt_atualizacao_nrec,
						nm_usuario_nrec,
						qt_tempo_preparo,
						qt_tempo_execucao)
			values (nextval('com_solic_sd_mod_seq'),
				nr_seq_modulo_w,
				nr_seq_solic_p,
				nm_usuario_p,
				clock_timestamp(),
				clock_timestamp(),
				nm_usuario_p,
				qt_tempo_preparo_w,
				qt_tempo_execucao_w);
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
