SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE com_gravar_software_sd_js ( ds_lista_softwares_p text, nr_seq_solic_p bigint) AS $body$
DECLARE


nr_seq_software_w	bigint;
tam_lista_w		bigint;
ie_pos_virgula_w	smallint := 0;
ds_lista_softwares_w	varchar(800);


BEGIN

ds_lista_softwares_w := substr(ds_lista_softwares_p,1,800);

if (coalesce(nr_seq_solic_p,0) > 0) and (ds_lista_softwares_w IS NOT NULL AND ds_lista_softwares_w::text <> '') then

	delete	FROM com_solic_soft
	where	nr_seq_solic = nr_seq_solic_p;
	commit;

	WHILE(ds_lista_softwares_w IS NOT NULL AND ds_lista_softwares_w::text <> '') LOOP
		begin

		tam_lista_w			:=	length(ds_lista_softwares_w);
		ie_pos_virgula_w		:=	position(',' in ds_lista_softwares_w);

		if (ie_pos_virgula_w <> 0) then
			nr_seq_software_w		:= substr(ds_lista_softwares_w,1,(ie_pos_virgula_w - 1));
			ds_lista_softwares_w	:= substr(ds_lista_softwares_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		insert into com_solic_soft(nr_sequencia,
						nr_seq_software,
						nr_seq_solic)
			values (nextval('com_solic_soft_seq'),
				nr_seq_software_w,
				nr_seq_solic_p);
		end;
	end loop;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
