SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE com_importar_modulos ( nr_seq_cliente_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_modulo_w		bigint;

C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from	modulo_implantacao a
	where	a.ie_comercial = 'S'
	and	not exists (	SELECT	1
				from	com_cliente_mod_impl x
				where	x.nr_seq_modulo = a.nr_sequencia
				and	x.nr_seq_cliente	= nr_seq_cliente_p)
	order by nm_modulo;


BEGIN

open C01;
loop
fetch C01 into
	nr_seq_modulo_w;
EXIT WHEN NOT FOUND; /* apply on C01 */
	begin

	insert into com_cliente_mod_impl(	NR_SEQUENCIA,
						NR_SEQ_CLIENTE,
						DT_ATUALIZACAO,
						NM_USUARIO,
						NR_SEQ_MODULO,
						PR_UTILIZACAO)
					values ( nextval('com_cliente_mod_impl_seq'),
						nr_seq_cliente_p,
						clock_timestamp(),
						nm_usuario_p,
						nr_seq_modulo_w,
						0);

	end;
end loop;
close C01;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
