CREATE OR REPLACE PROCEDURE com_inserir_funcao_impl ( nr_seq_mod_impl_p bigint, ds_lista_funcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ds_lista_funcao_w		varchar(800);
cd_funcao_w			bigint;
tam_lista_w			bigint;
ie_pos_virgula_w		smallint	:= 0;
pr_funcao_modulo_w		double precision;


BEGIN

ds_lista_funcao_w	:= ds_lista_funcao_p;
if (ds_lista_funcao_w IS NOT NULL AND ds_lista_funcao_w::text <> '') then
	begin
	WHILE(ds_lista_funcao_w IS NOT NULL AND ds_lista_funcao_w::text <> '') LOOP
		begin

		tam_lista_w			:=	length(ds_lista_funcao_w);
		ie_pos_virgula_w		:=	position(',' in ds_lista_funcao_w);

		if (ie_pos_virgula_w <> 0) then
			cd_funcao_w		:= substr(ds_lista_funcao_w,1,(ie_pos_virgula_w - 1));
			ds_lista_funcao_w	:= substr(ds_lista_funcao_w,(ie_pos_virgula_w + 1),tam_lista_w);
		end if;

		select	nextval('com_cliente_fun_impl_seq')
		into STRICT	nr_sequencia_w
		;

		insert	into com_cliente_fun_impl(
			nr_sequencia,
			nr_seq_mod_impl,
			dt_atualizacao,
			nm_usuario,
			cd_funcao,
			dt_inicio_util)
		values ( nr_sequencia_w,
			nr_seq_mod_impl_p,
			clock_timestamp(),
			nm_usuario_p,
			cd_funcao_w,
			clock_timestamp());

		end;
	END loop;
	end;

	select	coalesce(sum(pr_funcao_modulo),0)
	into STRICT	pr_funcao_modulo_w
	from	funcao a,
		com_cliente_fun_impl b
	where	a.cd_funcao		= b.cd_funcao
	and	b.nr_seq_mod_impl	= nr_seq_mod_impl_p;

	update	com_cliente_mod_impl
	set	pr_utilizacao		= pr_funcao_modulo_w,
		dt_atualizacao	= clock_timestamp(),
		nm_usuario		= nm_usuario_p
	where	nr_sequencia		= nr_seq_mod_impl_p;

	commit;
end if;

END;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
