SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE com_inserir_lead_cad (ds_lead_p text, nm_usuario_p text) AS $body$
DECLARE


qt_registros_w	bigint;

BEGIN

select 	count(*)
into STRICT 	qt_registros_w
from	com_leads
where	upper(ds_descricao) = upper(ds_lead_p);

if qt_registros_w = 0 then
	insert into COM_LEADS(nr_sequencia,
				 dt_atualizacao,
				 nm_usuario,
				 dt_atualizacao_nrec,
				 nm_usuario_nrec,
				 ie_situacao,
				 ds_descricao)
	values (nextval('com_leads_seq'),
				 clock_timestamp(),
				 nm_usuario_p,
				 clock_timestamp(),
				 nm_usuario_p,
				 'A',
				 substr(ds_lead_p,1,255));

end if;


commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
