CREATE OR REPLACE PROCEDURE com_insert_func_impl ( nr_seq_mod_impl_p bigint, cd_funcao_p text, nm_usuario_p text ) AS $body$
DECLARE


tam_lista_w			bigint;
ie_pos_virgula_w		smallint	:= 0;
pr_funcao_modulo_w		double precision;


BEGIN
	if (cd_funcao_p IS NOT NULL AND cd_funcao_p::text <> '') then
		begin

			insert	into com_cliente_fun_impl(
				nr_sequencia,
				nr_seq_mod_impl,
				dt_atualizacao,
				nm_usuario,
				cd_funcao,
				dt_inicio_util)
			values ( nextval('com_cliente_fun_impl_seq'),
				nr_seq_mod_impl_p,
				clock_timestamp(),
				nm_usuario_p,
				cd_funcao_p,
				clock_timestamp());

			select	coalesce(sum(pr_funcao_modulo),0)
			into STRICT	pr_funcao_modulo_w
			from	funcao a,
				com_cliente_fun_impl b
			where	a.cd_funcao		= b.cd_funcao
			and	b.nr_seq_mod_impl	= nr_seq_mod_impl_p;

			update	com_cliente_mod_impl
			set	pr_utilizacao	= pr_funcao_modulo_w,
				dt_atualizacao	= clock_timestamp(),
				nm_usuario	= nm_usuario_p
			where	nr_sequencia	= nr_seq_mod_impl_p;

			commit;
		end;
	end if;
end;
