CREATE OR REPLACE PROCEDURE conciliar_parcelas_cartao_cr ( nr_seq_extrato_p bigint, ds_lista_extrato_movto_p text, ds_lista_parcela_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_parcela_w		bigint;
vl_tot_extrato_movto_w	double precision;
vl_tot_parcela_w		double precision;
nr_seq_extrato_parcela_w	bigint;


BEGIN

select	coalesce(sum(a.vl_parcela),0)
into STRICT	vl_tot_extrato_movto_w
from	extrato_cartao_cr_movto a
where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || a.nr_sequencia || ' %'
and	(ds_lista_extrato_movto_p IS NOT NULL AND ds_lista_extrato_movto_p::text <> '');

select	coalesce(sum(a.vl_parcela),0)
into STRICT	vl_tot_parcela_w
from	movto_cartao_cr_parcela a
where	' ' || ds_lista_parcela_p || ' ' like '% ' || a.nr_sequencia || ' %'
and	(ds_lista_parcela_p IS NOT NULL AND ds_lista_parcela_p::text <> '');

/* Conciliar */

if (ie_opcao_p = 'C') and (vl_tot_extrato_movto_w = vl_tot_parcela_w) then

	select	nextval('extrato_cartao_cr_parcela_seq')
	into STRICT	nr_seq_extrato_parcela_w
	;

	insert	into	extrato_cartao_cr_parcela(nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_extrato)
	values (nr_seq_extrato_parcela_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_extrato_p);

	update	extrato_cartao_cr_movto
	set	nr_seq_extrato_parcela	= nr_seq_extrato_parcela_w
	where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %';

	update	movto_cartao_cr_parcela
	set	nr_seq_extrato_parcela	= nr_seq_extrato_parcela_w
	where	' ' || ds_lista_parcela_p || ' ' like '% ' || nr_sequencia || ' %';

elsif (ie_opcao_p = 'D') then

	update	movto_cartao_cr_parcela
	set	nr_seq_extrato_parcela	 = NULL
	where	(nr_seq_extrato_parcela IS NOT NULL AND nr_seq_extrato_parcela::text <> '')
	and	nr_seq_extrato_parcela	in (SELECT	distinct nr_seq_extrato_parcela
						from	extrato_cartao_cr_movto
						where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %');

	update	extrato_cartao_cr_movto
	set	nr_seq_extrato_parcela	 = NULL
	where	(nr_seq_extrato_parcela IS NOT NULL AND nr_seq_extrato_parcela::text <> '')
	and	nr_seq_extrato_parcela	in (SELECT	distinct nr_seq_extrato_parcela
						from	extrato_cartao_cr_movto
						where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %');

	delete	from	extrato_cartao_cr_parcela
	where	nr_sequencia in (SELECT	distinct nr_seq_extrato_parcela
				from	extrato_cartao_cr_movto
				where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %');
else
	CALL wheb_mensagem_pck.exibir_mensagem_abort(282224);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
