CREATE OR REPLACE PROCEDURE concil_parcel_cartao_redecard ( nr_seq_extrato_arq_p bigint, ds_lista_extrato_movto_p text, ds_lista_parcela_p text, ie_opcao_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_parcela_w		bigint;
vl_tot_extrato_movto_w		double precision;
vl_tot_parcela_w		double precision;
nr_seq_extrato_parcela_w	bigint;
nr_seq_extrato_w		bigint;
ie_tipo_arquivo_w		varchar(5);
vl_total_aconciliar_w		double precision;


BEGIN

select	max(nr_seq_extrato),
	max(ie_tipo_arquivo)
into STRICT	nr_seq_extrato_w,
	ie_tipo_arquivo_w
from	extrato_cartao_cr_arq
where	nr_sequencia = nr_seq_extrato_arq_p;

select	coalesce(sum(coalesce(a.vl_liquido,a.vl_parcela)),0)
into STRICT	vl_tot_extrato_movto_w
from	extrato_cartao_cr_movto a
where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || a.nr_sequencia || ' %'
and	(ds_lista_extrato_movto_p IS NOT NULL AND ds_lista_extrato_movto_p::text <> '');

select	coalesce(sum(a.vl_liquido),0)
into STRICT	vl_tot_parcela_w
from	movto_cartao_cr b,
	movto_cartao_cr_parcela a
where (ie_tipo_arquivo_w <> 'F' or coalesce(b.dt_cancelamento::text, '') = '')
and	' ' || ds_lista_parcela_p || ' ' like '% ' || a.nr_sequencia || ' %'
and	(ds_lista_parcela_p IS NOT NULL AND ds_lista_parcela_p::text <> '')
and	a.nr_seq_movto		= b.nr_sequencia
and	b.ie_tipo_cartao	= CASE WHEN ie_tipo_arquivo_w='F' THEN 'C'  ELSE ie_tipo_arquivo_w END;

if (ie_opcao_p = 'C') and (vl_tot_extrato_movto_w = vl_tot_parcela_w) then

	select	nextval('extrato_cartao_cr_parcela_seq')
	into STRICT	nr_seq_extrato_parcela_w
	;

	insert	into extrato_cartao_cr_parcela(nr_sequencia,
		dt_atualizacao_nrec,
		nm_usuario_nrec,
		dt_atualizacao,
		nm_usuario,
		nr_seq_extrato_arq,
		nr_seq_extrato)
	values (nr_seq_extrato_parcela_w,
		clock_timestamp(),
		nm_usuario_p,
		clock_timestamp(),
		nm_usuario_p,
		nr_seq_extrato_arq_p,
		nr_seq_extrato_w);

	if (ie_tipo_arquivo_w = 'F') then

		select	coalesce(sum(a.vl_liquido),0)
		into STRICT	vl_total_aconciliar_w
		from	movto_cartao_cr_parcela a
		where	' ' || ds_lista_parcela_p || ' ' like '% ' || a.nr_sequencia || ' %'
		and	exists (SELECT	1
			from	movto_cartao_cr x
			where (ie_tipo_arquivo_w <> 'F' or coalesce(x.dt_cancelamento::text, '') = '')
			and	x.nr_sequencia		= a.nr_seq_movto
			and	x.ie_tipo_cartao	= CASE WHEN ie_tipo_arquivo_w='F' THEN 'C'  ELSE ie_tipo_arquivo_w END );

	else
		vl_total_aconciliar_w	:= 0;

	end if;

	update	extrato_cartao_cr_movto
	set	nr_seq_extrato_parcela	= nr_seq_extrato_parcela_w,
		vl_saldo_concil_fin	= coalesce(vl_saldo_concil_fin,0) - coalesce(vl_total_aconciliar_w,0)
	where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %';

	update	movto_cartao_cr_parcela a
	set	a.nr_seq_extrato_parcela	= nr_seq_extrato_parcela_w
	where	' ' || ds_lista_parcela_p || ' ' like '% ' || a.nr_sequencia || ' %'
	and	exists (SELECT	1
		from	movto_cartao_cr x
		where (ie_tipo_arquivo_w <> 'F' or coalesce(x.dt_cancelamento::text, '') = '')
		and	x.nr_sequencia		= a.nr_seq_movto
		and	x.ie_tipo_cartao	= CASE WHEN ie_tipo_arquivo_w='F' THEN 'C'  ELSE ie_tipo_arquivo_w END );

elsif (ie_opcao_p = 'D') then

	if (ie_tipo_arquivo_w = 'F') then

		select	coalesce(sum(a.vl_liquido),0)
		into STRICT	vl_total_aconciliar_w
		from	movto_cartao_cr_parcela a
		where	a.nr_seq_extrato_parcela in (SELECT	distinct
								x.nr_seq_extrato_parcela
							from	extrato_cartao_cr_movto x
							where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || x.nr_sequencia || ' %')
		and	exists (select	1
			from	movto_cartao_cr x
			where	x.nr_sequencia		= a.nr_seq_movto
			and	x.ie_tipo_cartao	= CASE WHEN ie_tipo_arquivo_w='F' THEN 'C'  ELSE ie_tipo_arquivo_w END );

	else
		vl_total_aconciliar_w	:= 0;

	end if;

	update	movto_cartao_cr_parcela a
	set	nr_seq_extrato_parcela	 = NULL
	where	(nr_seq_extrato_parcela IS NOT NULL AND nr_seq_extrato_parcela::text <> '')
	and	nr_seq_extrato_parcela	in (SELECT	distinct nr_seq_extrato_parcela
						from	extrato_cartao_cr_movto
						where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %')
	and	exists (select	1
		from	movto_cartao_cr x
		where	x.nr_sequencia		= a.nr_seq_movto
		and	x.ie_tipo_cartao	= CASE WHEN ie_tipo_arquivo_w='F' THEN 'C'  ELSE ie_tipo_arquivo_w END );

	update	extrato_cartao_cr_movto
	set	nr_seq_extrato_parcela	 = NULL,
		vl_saldo_concil_fin	= coalesce(vl_saldo_concil_fin,0) + coalesce(vl_total_aconciliar_w,0)
	where	(nr_seq_extrato_parcela IS NOT NULL AND nr_seq_extrato_parcela::text <> '')
	and	nr_seq_extrato_parcela	in (SELECT	distinct nr_seq_extrato_parcela
						from	extrato_cartao_cr_movto
						where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %');

	delete	from	extrato_cartao_cr_parcela
	where	nr_sequencia in (SELECT	distinct nr_seq_extrato_parcela
				from	extrato_cartao_cr_movto
				where	' ' || ds_lista_extrato_movto_p || ' ' like '% ' || nr_sequencia || ' %');
else
	/* O valor dos itens do extrato selecionados n√£o conferem com o valor das parcelas selecionadas!
	Vl extrato: vl_tot_extrato_movto_w
	Vl parcela: vl_tot_parcela_w */
	wheb_mensagem_pck.exibir_mensagem_abort(262439,	'VL_TOT_EXTRATO_MOVTO_W='||vl_tot_extrato_movto_w || ';' ||
							'VL_TOT_PARCELA_W=' || vl_tot_parcela_w);

end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE concil_parcel_cartao_redecard ( nr_seq_extrato_arq_p bigint, ds_lista_extrato_movto_p text, ds_lista_parcela_p text, ie_opcao_p text, nm_usuario_p text) FROM PUBLIC;

