SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE confere_faturamento_fleury ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_procedimento_p bigint, nm_exame_p text, cd_exame_p text, nr_controle_p text, nm_usuario_p text) AS $body$
DECLARE

 
qt_prescricao_w			bigint;
nr_controle_w			bigint;
nr_seq_exame_w			bigint;
nm_exame_w			varchar(80);
dt_baixa_w			timestamp;
ie_tipo_atendimento_w 		smallint;
cd_exame_w			varchar(20);
ie_status_exame_w			smallint;
nr_seq_prescricao_w		integer;
		
			 

BEGIN 
 
select 	count(*) 
into STRICT	qt_prescricao_w 
from 	procedimento_paciente 
where 	nr_atendimento = nr_atendimento_p 
and 	nr_prescricao = nr_prescricao_p 
and 	cd_procedimento = cd_procedimento_p;
 
if (qt_prescricao_w = 0) then 
 
	/* OS 309475 - inicio */
 
	 
	select	coalesce(max(nr_sequencia),0) 
	into STRICT	nr_seq_prescricao_w 
	from	prescr_procedimento 
	where 	nr_prescricao = nr_prescricao_p 
	and 	cd_procedimento = cd_procedimento_p;	
	 
	 
	if (coalesce(nr_seq_prescricao_w,0) > 0) then 
		 
		select	coalesce(max(nr_controle),0) 
		into STRICT	nr_controle_w 
		from	prescr_medica 
		where	nr_prescricao = nr_prescricao_p;
		 
		nm_exame_w	:= null;
		 
		begin 
		select	nr_seq_exame, 
			substr(obter_desc_exame(nr_seq_exame),1,80), 
			null 
		into STRICT	nr_seq_exame_w, 
			nm_exame_w, 
			dt_baixa_w 
		from 	prescr_procedimento 
		where 	nr_prescricao 	= nr_prescricao_p 
		and 	nr_sequencia 	= nr_seq_prescricao_w;
		exception 
			when others then 
			dt_baixa_w 	:= null;
			nm_exame_w	:= null;
		end;
			 
		if (coalesce(nr_seq_exame_w,0) = 0) then 
			begin 
			select	substr(obter_descricao_procedimento(cd_procedimento,ie_origem_proced),1,80) 
			into STRICT	nm_exame_w 
			from 	prescr_procedimento 
			where 	nr_prescricao 	= nr_prescricao_p 
			and 	nr_sequencia 	= nr_seq_prescricao_w;
			exception 
				when others then 
				nm_exame_w := null;
			end;
		end if;
		 
		select	coalesce(max(cd_exame),0) 
		into STRICT	cd_exame_w 
		from	exame_laboratorio 
		where	nr_seq_exame = nr_seq_exame_w;
		 
		select 	coalesce(max(ie_tipo_atendimento),0) 
		into STRICT	ie_tipo_atendimento_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
	 
		select	coalesce(max(ie_status_atend),0) 
		into STRICT	ie_status_exame_w 
		from	prescr_procedimento b, 
			prescr_medica a 
		where	a.nr_prescricao	= b.nr_prescricao 
		and	b.nr_prescricao	= nr_prescricao_p 
		and	b.nr_sequencia	= nr_seq_prescricao_w;
			 
	else 
 
		nm_exame_w := nm_exame_p;
		cd_exame_w := cd_exame_p;
		nr_controle_w := nr_controle_p;
		dt_baixa_w := clock_timestamp();
		 
	 
	end if;
	 
	/* OS 309475 - fim */
 
 
	/* NÃ£o executado */
 
	insert into w_conferencia_fleury(dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_prescricao, 
		cd_procedimento, 
		nr_atendimento, 
		IE_EXECUTADO_TASY, 
		nr_controle, 
		nr_seq_exame, 
		nm_exame, 
		dt_baixa, 
		ie_tipo_atendimento, 
		cd_exame, 
		ie_status_exame ) 
	values (clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_prescricao_p, 
		cd_procedimento_p, 
		nr_atendimento_p, 
		'N', 
		nr_controle_w, 
		nr_seq_exame_w, 
		nm_exame_w, 
		dt_baixa_w, 
		ie_tipo_atendimento_w, 
		cd_exame_w, 
		ie_status_exame_w);
		 
else 
	/* Executado */
 
		 
	select	coalesce(max(nr_sequencia_prescricao),0) 
	into STRICT	nr_seq_prescricao_w 
	from	procedimento_paciente 
	where 	nr_atendimento = nr_atendimento_p 
	and 	nr_prescricao = nr_prescricao_p 
	and 	cd_procedimento = cd_procedimento_p;	
	 
	/* OS 309475 - inicio */
 
	if (coalesce(nr_seq_prescricao_w,0) > 0) then 
		 
		select	coalesce(max(nr_controle),0) 
		into STRICT	nr_controle_w 
		from	prescr_medica 
		where	nr_prescricao = nr_prescricao_p;
		 
		begin 
		select	nr_seq_exame, 
			substr(obter_desc_exame(nr_seq_exame),1,80), 
			dt_procedimento 
		into STRICT	nr_seq_exame_w, 
			nm_exame_w, 
			dt_baixa_w 
		from 	procedimento_paciente 
		where 	nr_prescricao 			= nr_prescricao_p 
		and 	nr_sequencia_prescricao = nr_seq_prescricao_w;
		exception 
			when others then 
			dt_baixa_w := null;
		end;
		 
		select	coalesce(max(cd_exame),0) 
		into STRICT	cd_exame_w 
		from	exame_laboratorio 
		where	nr_seq_exame = nr_seq_exame_w;
		 
		select 	coalesce(max(ie_tipo_atendimento),0) 
		into STRICT	ie_tipo_atendimento_w 
		from	atendimento_paciente 
		where	nr_atendimento = nr_atendimento_p;
	 
		select	coalesce(max(ie_status_atend),0) 
		into STRICT	ie_status_exame_w 
		from	prescr_procedimento b, 
			prescr_medica a 
		where	a.nr_prescricao		= b.nr_prescricao 
		and	b.nr_prescricao		= nr_prescricao_p 
		and	b.nr_sequencia		= nr_seq_prescricao_w;
			 
	end if;
	/* OS 309475 - fim */
 
	 
	 
	insert into w_conferencia_fleury(dt_atualizacao, 
		nm_usuario, 
		dt_atualizacao_nrec, 
		nm_usuario_nrec, 
		nr_prescricao, 
		cd_procedimento, 
		nr_atendimento, 
		ie_executado_tasy, 
		nr_controle, 
		nr_seq_exame, 
		nm_exame, 
		dt_baixa, 
		ie_tipo_atendimento, 
		cd_exame, 
		ie_status_exame) 
	values (clock_timestamp(), 
		nm_usuario_p, 
		clock_timestamp(), 
		nm_usuario_p, 
		nr_prescricao_p, 
		cd_procedimento_p, 
		nr_atendimento_p, 
		'E', 
		nr_controle_w, 
		nr_seq_exame_w, 
		nm_exame_w, 
		dt_baixa_w, 
		ie_tipo_atendimento_w, 
		cd_exame_w, 
		ie_status_exame_w);
		 
end if;
 
commit;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE confere_faturamento_fleury ( nr_atendimento_p bigint, nr_prescricao_p bigint, cd_procedimento_p bigint, nm_exame_p text, cd_exame_p text, nr_controle_p text, nm_usuario_p text) FROM PUBLIC;

