CREATE OR REPLACE PROCEDURE conferir_tributos ( nr_seq_item_p bigint, nr_item_nf_p bigint, cd_tributo_p bigint, dt_vencimento_p timestamp, ie_opcao_p text, nm_usuario_p text) AS $body$
BEGIN
if (ie_opcao_p = 'I') then
	update 	nota_fiscal_item_trib
	set 	dt_conferencia  = clock_timestamp(),
		nm_usuario_conf = nm_usuario_p
	where 	nr_sequencia 	= nr_seq_item_p
	and	nr_item_nf	= nr_item_nf_p
	and	cd_tributo	= cd_tributo_p;
elsif (ie_opcao_p = 'F') then
	update 	nota_fiscal_trib
	set 	dt_conferencia 	= clock_timestamp(),
		nm_usuario_conf = nm_usuario_p
	where 	nr_seq_interno 	= nr_seq_item_p
	and	cd_tributo	= cd_tributo_p;
elsif (ie_opcao_p = 'V') then
	update 	nota_fiscal_venc_trib
	set 	dt_conferencia 	= clock_timestamp(),
		nm_usuario_conf = nm_usuario_p
	where 	nr_sequencia 	= nr_seq_item_p
	and	cd_tributo	= cd_tributo_p
	and	dt_vencimento	= dt_vencimento_p;
end if;

end;
$body$
