SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE configurar_grafico_pepo_html ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_lista_sequencia_w	varchar(500);
nr_sequencia_w          bigint;
cd_estabelecimento_w	bigint := wheb_usuario_pck.get_cd_estabelecimento;
nr_seq_protocolo_w 		protocolo_cirurgia.nr_sequencia%type;

C01 CURSOR FOR	
SELECT	a.nr_sequencia
from	pepo_sinal_vital a
where 	((a.cd_estabelecimento = cd_estabelecimento_w) or (coalesce(a.cd_estabelecimento::text, '') = ''))	
and 	coalesce(a.ie_situacao,'A') 	= 'A'
and   	not exists (SELECT	1
                                    from	   pepo_item_grafico x
                                    where	x.nr_seq_sinal_vital	=	a.nr_sequencia
                                    and	   (((x.nr_cirurgia	=	nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
                                                ((x.nr_seq_pepo	=	nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)))
                                    and	x.nm_usuario		=	nm_usuario_p);


BEGIN
open	C01;
	loop
	fetch C01 into	
		nr_sequencia_w;		
	EXIT WHEN NOT FOUND; /* apply on C01 */
 	begin
   		ds_lista_sequencia_w := ds_lista_sequencia_w || nr_sequencia_w || ',';
 	end;
	end loop;
close C01;

CALL Configurar_Grafico_PEPO(nr_cirurgia_p, nr_seq_pepo_p, nm_usuario_p);

	select max(nr_seq_protocolo)
	  into STRICT nr_seq_protocolo_w 
	  from cirurgia_protocolo_util 
	 where nr_sequencia = (SELECT max(nr_sequencia) 
							 from cirurgia_protocolo_util 
							where nr_cirurgia = nr_cirurgia_p);
							
if (ds_lista_sequencia_w IS NOT NULL AND ds_lista_sequencia_w::text <> '') then
   update pepo_item_grafico a set a.ie_grafico =  (SELECT max(coalesce(b.ie_padrao, 'S')) from pepo_sinal_vital b where b.nr_sequencia = a.nr_seq_sinal_vital)
   where   OBTER_SE_CONTIDO(a.nr_seq_sinal_vital, ds_lista_sequencia_w) = 'S'
   and	a.nm_usuario	=	nm_usuario_p
   and	(((a.nr_cirurgia	=	nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
				((a.nr_seq_pepo	=	nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)));
end if;

if (nr_seq_protocolo_w IS NOT NULL AND nr_seq_protocolo_w::text <> '') then
    update pepo_item_grafico a set a.ie_grafico =  (SELECT CASE WHEN count(1)=0 THEN 'N'  ELSE 'S' END  from protocolo_sinal_vital  psv
                                                     inner join pepo_sv ps on ps.nr_sequencia = psv.nr_seq_sinal_vital
                                                     inner join pepo_sinal_vital pepo on pepo.ie_sinal_vital = ps.ie_sinal_vital
                                                     where psv.nr_seq_protocolo = nr_seq_protocolo_w
                                                       and pepo.nr_sequencia = a.nr_seq_sinal_vital)
    where  a.nm_usuario	=	nm_usuario_p
   and	(((a.nr_cirurgia	=	nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
				((a.nr_seq_pepo	=	nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)))
  and a.ie_grafico = 'N';
end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
