SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE configurar_grafico_pepo ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_sequencia_w			bigint;
ie_tipo_w			varchar(15);
ie_tipo_ww			bigint;
ie_modo_w			varchar(2);
nr_ordenacao_w			bigint;
ie_ordena_grafico_item_w	varchar(1);
ie_modo_grafico_w		varchar(15);
cd_estabelecimento_w	bigint := wheb_usuario_pck.get_cd_estabelecimento;

C01 CURSOR FOR
	SELECT	'A',
		a.nr_sequencia,
		ie_tipo,
		a.ie_modo_adm,
		CASE WHEN a.ie_modo_adm='CO' THEN 110  ELSE CASE WHEN ie_tipo=1 THEN 120 WHEN ie_tipo=2 THEN 130 WHEN ie_tipo=3 THEN 140 WHEN ie_tipo=5 THEN 200 WHEN coalesce(ie_tipo::text, '') = '' THEN 100  ELSE 115 END  END  nr_ordenacao
	from	cirurgia_agente_anestesico a
	where	(((nr_cirurgia = nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
		 ((nr_seq_pepo = nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)))
	and	ie_tipo in (1,2,3,5)
	and	coalesce(a.ie_situacao,'A') = 'A'
	and not exists (	SELECT	1
				from	pepo_item_grafico x
				where	x.nr_seq_cirur_agente	=	a.nr_sequencia
				and	x.nm_usuario		=	nm_usuario_p)
	
union

	select	'S',
		nr_sequencia,
		null,
		null,
		100 nr_ordenacao
	from	pepo_sinal_vital a
	where 	((a.cd_estabelecimento = cd_estabelecimento_w) or (coalesce(a.cd_estabelecimento::text, '') = ''))
	and 	coalesce(a.ie_situacao,'A') 	= 'A'
	and   	not exists (	select	1
				from	pepo_item_grafico x
				where	x.nr_seq_sinal_vital	=	a.nr_sequencia
				and	(((x.nr_cirurgia	=	nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
					 ((x.nr_seq_pepo	=	nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)))
				and	x.nm_usuario		=	nm_usuario_p)
	
union

	select	'PG',
		a.nr_seq_tipo,
		null,
		null,
		300 nr_ordenacao
	from	atendimento_perda_ganho a,
		tipo_perda_ganho b
	where	a.nr_seq_tipo 		= b.nr_sequencia
	and	b.ie_exibir_grafico 	= 'S'
	and	(((a.nr_cirurgia = nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
		 ((a.nr_seq_pepo = nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)))
	and	coalesce(a.ie_situacao,'A') = 'A'
	and not exists (	select	1
				from	pepo_item_grafico x
				where	x.nr_seq_tipo	=	a.nr_seq_tipo
				and	(((x.nr_cirurgia	=	nr_cirurgia_p) and (coalesce(nr_cirurgia_p,0) > 0)) or
					 ((x.nr_seq_pepo	=	nr_seq_pepo_p) and (coalesce(nr_seq_pepo_p,0) > 0)))
				and	x.nm_usuario		=	nm_usuario_p)	;

BEGIN

ie_ordena_grafico_item_w := obter_param_usuario(872, 229, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_ordena_grafico_item_w);
ie_modo_grafico_w := obter_param_usuario(872, 136, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_w, ie_modo_grafico_w);

	OPEN	C01;
	LOOP
	FETCH C01 into
		ie_tipo_w,
		nr_sequencia_w,
		ie_tipo_ww,
		ie_modo_w,
		nr_ordenacao_w;
	EXIT WHEN NOT FOUND; /* apply on c01 */
 	begin

	insert into pepo_item_grafico(
		nr_sequencia,
  		nr_seq_sinal_vital,
  		nr_seq_cirur_agente,
		nr_seq_tipo,
  		ie_grafico,
  		nr_seq_apresentacao,
  		dt_atualizacao,
  		nm_usuario,
		nr_cirurgia,
		nr_seq_pepo)
 	values (nextval('pepo_item_grafico_seq'),
  		CASE WHEN ie_tipo_w='S' THEN nr_sequencia_w END ,
  		CASE WHEN ie_tipo_w='A' THEN nr_sequencia_w END ,
		CASE WHEN ie_tipo_w='PG' THEN nr_sequencia_w END ,
	  	'S',
	  	CASE WHEN ie_ordena_grafico_item_w='N' THEN CASE WHEN ie_modo_w='CO' THEN 110  ELSE CASE WHEN ie_tipo_ww=5 THEN 200 WHEN coalesce(ie_tipo_ww::text, '') = '' THEN 100  ELSE 115 END  END   ELSE nr_ordenacao_w END ,
	  	clock_timestamp(),
	  	nm_usuario_p,
		nr_cirurgia_p,
		nr_seq_pepo_p);
 	end;
	END LOOP;
	CLOSE C01;

Commit;

if (ie_modo_grafico_w = 'G') then
	CALL definir_ordem_grupo_pepo(nr_cirurgia_p, nr_seq_pepo_p, nm_usuario_p);
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE configurar_grafico_pepo ( nr_cirurgia_p bigint, nr_seq_pepo_p bigint, nm_usuario_p text) FROM PUBLIC;

