SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE confirmar_data_de_envio_js (nr_sequencia_p bigint, ie_opcao_p text) AS $body$
BEGIN

if (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '')then
	begin

	if (ie_opcao_p = 'C')then
		begin

		update	lote_cih
		set 	ie_status = 'E',
			dt_envio = clock_timestamp()
		where	nr_sequencia = nr_sequencia_p;

		end;
	end if;

	if (ie_opcao_p = 'D')then
		begin

		update	lote_cih
		set 	ie_status = 'A',
			dt_envio  = NULL
		where	nr_sequencia = nr_sequencia_p;

		end;
	end if;

	end;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
