CREATE OR REPLACE PROCEDURE confirmar_data_prev_quimio ( nr_seq_paciente_p bigint, nr_seq_atendimento_p bigint, nm_usuario_p text) AS $body$
DECLARE

 
ds_erro_w	varchar(2000);


BEGIN 
if (nr_seq_paciente_p IS NOT NULL AND nr_seq_paciente_p::text <> '') and (nr_seq_atendimento_p IS NOT NULL AND nr_seq_atendimento_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then 
	begin 
	 
	ds_erro_w := gerar_agend_serv_pac_atend( 
		nr_seq_paciente_p, nr_seq_atendimento_p, nm_usuario_p, ds_erro_w);
	 
	update	paciente_atendimento 
	set	dt_real	= dt_prevista 
	where	coalesce(dt_real::text, '') = '' 
	and	nr_seq_paciente = nr_seq_paciente_p 
	and	coalesce(substr(Qt_Obter_Status_Agenda(nr_seq_atendimento),1,50),'XPTO') <> 'F' 
	and coalesce(dt_liberacao_reg::text, '') = '';	
	end;
end if;
commit;
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
