CREATE OR REPLACE PROCEDURE confirmar_efetuar_marcacao_js ( nr_seq_pend_agenda_p bigint, nr_seq_texto_p bigint, qt_dias_pendencia_p INOUT bigint, qt_dias_marcacao_p INOUT bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE



qt_dias_pendencia_w	bigint		:= 0;
qt_dias_marcacao_w	bigint		:= 0;
ds_mensagem_w		varchar(255)		:= '';


BEGIN

select	count(*)
into STRICT	qt_dias_pendencia_w
from	paciente_atendimento a
where	a.nr_seq_pend_agenda	= nr_seq_pend_agenda_p;

select	count(*)
into STRICT	qt_dias_marcacao_w
from	agenda_quimio_marcacao a
where	a.nr_seq_pend_agenda = nr_seq_pend_agenda_p;

ds_mensagem_w	:= substr(obter_texto_tasy(nr_seq_texto_p, null),1,255);

qt_dias_pendencia_p	:= qt_dias_pendencia_w;
qt_dias_marcacao_p	:= qt_dias_marcacao_w;
ds_mensagem_p		:= ds_mensagem_w;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
