CREATE OR REPLACE PROCEDURE confirmar_entrega_ordem_compra ( nr_ordem_compra_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_classif_w			bigint;
ds_titulo_w				varchar(80);
ds_mensagem_w				varchar(2000);
nm_usuario_destino_w			varchar(15);


BEGIN

select	substr(obter_usuario_pessoa(cd_pessoa_solicitante),1,15)
into STRICT	nm_usuario_destino_w
from	ordem_compra
where	nr_ordem_compra = nr_ordem_compra_p;

update	ordem_compra
set	dt_confirma_entrega 	= clock_timestamp(),
	nm_usuario_conf_entr	= nm_usuario_p
where	nr_ordem_compra		= nr_ordem_compra_p;

if (nm_usuario_destino_w IS NOT NULL AND nm_usuario_destino_w::text <> '') then

	select	max(obter_classif_comunic('F'))
	into STRICT	nr_seq_classif_w
	;

	ds_titulo_w		:= WHEB_MENSAGEM_PCK.get_texto(298454,'NR_ORDEM_COMPRA_P='||nr_ordem_compra_p);
	ds_mensagem_w		:= WHEB_MENSAGEM_PCK.get_texto(298455,'NR_ORDEM_COMPRA_P='||nr_ordem_compra_p);

	CALL gerar_comunic_padrao(
		clock_timestamp(),
		ds_titulo_w,
		ds_mensagem_w,
		nm_usuario_p,
		'N',
		nm_usuario_destino_w,
		'N',
		nr_seq_classif_w,
		null,
		null,
		null,
		clock_timestamp(),
		null,
		null);
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
