SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE confirmar_intervencoes ( nr_sequencia_p bigint, nm_usuario_p text, ie_selecionada_p text, ie_opcao_p text) AS $body$
DECLARE

/*Opção
'VE' 	- Verificar existência de confirmada e excluir as não confirmadas
'M' 	- Marcar/ desmarcar / Excluir intervênções selecionadas
*/
qt_selecionada_w	bigint;


BEGIN

if (ie_opcao_p = 'M') then

	If (ie_selecionada_p <> 'E') then
		update	pe_prescr_proc
		set	ie_confirmada = ie_selecionada_p
		where	nr_sequencia = nr_sequencia_p;
	else
		delete from pe_prescr_proc
		where	nr_sequencia = nr_sequencia_p;

	end if;

elsif (ie_opcao_p = 'VE') then

	select 	count(*)
	into STRICT	qt_selecionada_w
	from	pe_prescr_proc
	where	nr_seq_prescr = nr_sequencia_p
	and	ie_confirmada = 'S';

	if (qt_selecionada_w > 0) then

		delete 	from pe_prescr_proc
		where	nr_seq_prescr = nr_sequencia_p
		and	((ie_confirmada = 'N') or (coalesce(ie_confirmada::text, '') = ''));

	end if;

end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
