CREATE OR REPLACE PROCEDURE confirmar_pre_agenda ( nr_sequencia_p bigint, ie_status_agenda_p text) AS $body$
DECLARE


nr_seq_proc_interno_w	bigint;
qt_dias_prev_inter_w	smallint;


BEGIN

update		agenda_paciente
set		ie_status_agenda	= coalesce(ie_status_agenda_p,'PC')
where		nr_sequencia	=	nr_sequencia_p;

select	max(nr_seq_proc_interno)
into STRICT	nr_seq_proc_interno_w
from	agenda_paciente
where	nr_sequencia	=	nr_sequencia_p;

if (nr_seq_proc_interno_w IS NOT NULL AND nr_seq_proc_interno_w::text <> '') then

	select 	max(qt_dias_prev_inter)
	into STRICT	qt_dias_prev_inter_w
	from 	proc_interno
	where 	nr_sequencia = nr_seq_proc_interno_w;

	update 	agenda_paciente
	set	QT_DIARIA_PREV = qt_dias_prev_inter_w
	where	nr_sequencia	=	nr_sequencia_p;

end if;
commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
