CREATE OR REPLACE PROCEDURE confirmar_probl_colaborativo ( nr_prescricao_p bigint, ds_lista_probl_colab_p text, nm_usuario_p text) AS $body$
DECLARE


nr_seq_probl_colab_w	bigint;
nr_pos_virgula_w	bigint;
ds_lista_probl_colab_w	varchar(2000);


BEGIN
if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (ds_lista_probl_colab_p IS NOT NULL AND ds_lista_probl_colab_p::text <> '') and (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') then
	begin

	ds_lista_probl_colab_w	:= ds_lista_probl_colab_p;

	while(ds_lista_probl_colab_w IS NOT NULL AND ds_lista_probl_colab_w::text <> '') loop
		begin

		nr_pos_virgula_w := position(',' in ds_lista_probl_colab_w);

		if (nr_pos_virgula_w > 0) then
			begin
			nr_seq_probl_colab_w 	:= (substr(ds_lista_probl_colab_w,1,nr_pos_virgula_w-1))::numeric;
			ds_lista_probl_colab_w	:= substr(ds_lista_probl_colab_w,nr_pos_virgula_w+1,length(ds_lista_probl_colab_w));

			if (nr_seq_probl_colab_w > 0) then
				begin
				CALL gerar_probl_colab_enfermagem(
					nr_prescricao_p,
					nr_seq_probl_colab_w,
					nm_usuario_p);
				end;
			end if;
			end;
		else
			begin
			ds_lista_probl_colab_w := null;
			end;
		end if;
		end;
	end loop;
	end;
end if;
commit;
end;
$body$
