CREATE OR REPLACE PROCEDURE confirmar_transferencia_ordem ( nr_seq_viagem_p bigint, nr_sequencia_p bigint, nm_usuario_transf_p text, ie_estagio_p text, nm_usuario_p text) AS $body$
DECLARE


/* IE_ESTAGIO_P
R  - Recebimento
S  - Sa√≠da
E -  Entrada
*/
BEGIN

if (ie_estagio_p = 'R')	 then
	begin
	update	viagem_transf_destino
	set 	dt_retorno = clock_timestamp(),
		nm_usuario_retorno = nm_usuario_transf_p
	where	nr_seq_viagem = nr_seq_viagem_p
	and	nr_sequencia = nr_sequencia_p;
	end;

elsif (ie_estagio_p = 'S')	 then
	begin
	update	viagem_transf_destino
	set 	dt_saida = clock_timestamp(),
		nm_usuario_saida = nm_usuario_transf_p
	where	nr_seq_viagem = nr_seq_viagem_p
	and	nr_sequencia = nr_sequencia_p;
	end;

elsif (ie_estagio_p = 'E') then
	begin
	update	viagem_transf_destino
	set 	dt_entrega = clock_timestamp(),
		nm_usuario_entrega = nm_usuario_transf_p
	where	nr_seq_viagem = nr_seq_viagem_p
	and	nr_sequencia = nr_sequencia_p;
	end;
end if;

commit;

end;
$body$
