SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE confirma_agenda_paciente ( nr_seq_agenda_p bigint, nm_usuario_p text) AS $body$
DECLARE


ds_valor_parametro_w	varchar(255);
cd_pais_w   		smallint;
ie_posicao_w            posicao_cirurgia.ie_posicao%type;
nr_cirurgia_w		agenda_paciente.nr_cirurgia%type;
cd_medico_w		agenda_paciente.cd_medico%type;


BEGIN
if (nm_usuario_p IS NOT NULL AND nm_usuario_p::text <> '') and (nr_seq_agenda_p IS NOT NULL AND nr_seq_agenda_p::text <> '') then
	begin
	ds_valor_parametro_w := obter_param_usuario(871, 524, obter_perfil_ativo, nm_usuario_p, wheb_usuario_pck.get_cd_estabelecimento, ds_valor_parametro_w);
	if (ds_valor_parametro_w IS NOT NULL AND ds_valor_parametro_w::text <> '') then
		begin
		update	agenda_paciente
		set	dt_confirmacao		= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			ie_status_agenda		= ds_valor_parametro_w,
			nm_usuario_confirm	= nm_usuario_p,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;
		end;
	else
		begin
		update	agenda_paciente
		set	dt_confirmacao		= clock_timestamp(),
			dt_atualizacao		= clock_timestamp(),
			nm_usuario_confirm	= nm_usuario_p,
			nm_usuario		= nm_usuario_p
		where	nr_sequencia		= nr_seq_agenda_p;
		end;
	end if;
	end;
end if;

 select coalesce(Obter_Nr_Seq_Locale(nm_usuario_p),0)
 into STRICT   cd_pais_w
;

 if (cd_pais_w = 7) then
     CALL GERAR_CIRURGIA_AGENDA_HTML5(nr_seq_agenda_p);

     select coalesce(obter_posicao_agenda_pac(nr_seq_agenda_p),0)
     into STRICT   ie_posicao_w
;

	if (ie_posicao_w > 0) then
		select  coalesce(max(nr_cirurgia),0),
			max(cd_medico)
		into STRICT    nr_cirurgia_w,
			cd_medico_w
		from    agenda_paciente
		where   nr_sequencia = nr_seq_agenda_p;

		CALL gerar_posicao_cirurgia_pepo(nr_cirurgia_w, cd_medico_w, ie_posicao_w, nm_usuario_p);
	end if;

 end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
