CREATE OR REPLACE PROCEDURE considera_item_consistido ( nr_prescricao_p bigint, nr_sequencia_p bigint, ie_considera_p text, cd_estabelecimento_p bigint, nm_usuario_p text ) AS $body$
DECLARE


-- ie_considera_p
-- C - Considera
-- D - Desconsidera
BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') and (nr_sequencia_p IS NOT NULL AND nr_sequencia_p::text <> '') then
	if (ie_considera_p = 'C') then
		update	prescr_material
		set 	ie_status_cirurgia	= 'CB'
		where	cd_motivo_baixa 	= 0
		and	nr_prescricao 		= nr_prescricao_p
		and	nr_sequencia		= nr_sequencia_p;
	elsif (ie_considera_p = 'D') then
		update	prescr_material
		set 	ie_status_cirurgia	= 'GI'
		where	nr_prescricao 		= nr_prescricao_p
		and	nr_sequencia		= nr_sequencia_p;
	end if;
end if;

commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
