CREATE OR REPLACE PROCEDURE consisite_perm_duplicar_prescr ( nr_limite_copia_p bigint, nr_atendimento_p bigint, dt_validade_prescricao_p timestamp, ie_prescr_sem_diagnostico_p text, ie_alerta_validade_p text, ds_erro_p INOUT text, nm_usuario_p text, cd_prescritor_p bigint) AS $body$
DECLARE


ds_erro_w	varchar(2000);
qt_diag_w	bigint;


BEGIN
if (nr_limite_copia_p > 0) and (dt_validade_prescricao_p IS NOT NULL AND dt_validade_prescricao_p::text <> '') and
	(clock_timestamp() > (dt_validade_prescricao_p + nr_limite_copia_p/24)) then
	begin
	
	if (ie_alerta_validade_p = 'S') then
		begin
		-- Não é possível realizar a cópia desta prescrição! Prescrição inválida para cópia, localize a do dia anterior. Parâmetro [446] da REP.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(68659);
		end;
	else
		begin
		-- Não é possível realizar a cópia desta prescrição! Prescrição inválida para cópia, localize a do dia anterior. Parâmetro [446] da REP.
		ds_erro_w	:= obter_texto_dic_objeto(68659,wheb_usuario_pck.get_nr_seq_idioma,null);
		end;
	end if;
	
	end;
end if;

if (ie_prescr_sem_diagnostico_p <> 'S') then
	begin
	if (obter_qt_diag_atend(nr_atendimento_p) = 0) and
		((ie_prescr_sem_diagnostico_p = 'N') or
		((ie_prescr_sem_diagnostico_p = 'T') and (Obter_medico_resp_atend(nr_atendimento_p,'C') = cd_prescritor_p)))then
		begin
		-- Não é permitido fazer nova prescrição para paciente sem diagnóstico. Parâmetro [90] da REP.
		CALL wheb_mensagem_pck.exibir_mensagem_abort(68660);
		end;
	end if;
	end;
end if;


ds_erro_p := ds_erro_w;
commit;
end;
$body$
