CREATE OR REPLACE PROCEDURE consiste_acomp_categoria_lista ( nr_seq_servico_lista_p text, nr_atendimento_p bigint, dt_servico_p timestamp, nm_usuario_p text, cd_estabelecimento_p bigint, ds_mensagem_p INOUT text) AS $body$
DECLARE

 
ie_acomp_lib_eup_w		varchar(1);
nr_seq_servico_lista_w	varchar(2000);
nr_seq_servico_w		bigint;
ds_mensagem_w		varchar(255);
ds_pergunta_w		varchar(255);


BEGIN 
ds_mensagem_p		:= '';
nr_seq_servico_lista_w	:= nr_seq_servico_lista_p;
ds_pergunta_w		:= obter_texto_tasy(70497, wheb_usuario_pck.get_nr_seq_idioma);
 
/* Gestão da Nutrição - Parâmetro [5] - Ao inserir acompanhante verificar acompanhantes liberados na EUP */
 
ie_acomp_lib_eup_w := obter_param_usuario(1003, 5, obter_perfil_ativo, nm_usuario_p, cd_estabelecimento_p, ie_acomp_lib_eup_w);
 
while (nr_seq_servico_lista_w IS NOT NULL AND nr_seq_servico_lista_w::text <> '') loop 
	begin 
	nr_seq_servico_w		:= substr(nr_seq_servico_lista_w, 1, position(',' in nr_seq_servico_lista_w) - 1);
	nr_seq_servico_lista_w	:= substr(nr_seq_servico_lista_w, position(',' in nr_seq_servico_lista_w) + 1, length(nr_seq_servico_lista_w));
 
	ds_mensagem_w := consiste_acomp_categoria( 
		nr_seq_servico_w, nr_atendimento_p, nm_usuario_p, cd_estabelecimento_p, dt_servico_p, ds_mensagem_w);
 
	if (ds_mensagem_w IS NOT NULL AND ds_mensagem_w::text <> '') then 
		if (ie_acomp_lib_eup_w = 'Q') then 
			ds_mensagem_p	:= ds_mensagem_p || '#@#@' || ds_mensagem_w || ds_pergunta_w;
 
		elsif (ie_acomp_lib_eup_w = 'S') then 
			begin 
			nr_seq_servico_lista_w	:= '';
			ds_mensagem_p		:= ds_mensagem_w;
			end;
		end if;
	end if;
	end;
end loop;
 
if (ie_acomp_lib_eup_w = 'Q') then 
	ds_mensagem_p	:= substr(ds_mensagem_p, 5, length(ds_mensagem_p));
end if;
 
end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
