CREATE OR REPLACE PROCEDURE consiste_aferese_terapeutica ( nr_prescricao_p bigint, cd_estabelecimento_p bigint, nm_usuario_p text) AS $body$
DECLARE


nr_seq_prescr_w	integer;
C01 CURSOR FOR
	SELECT	a.nr_sequencia
	from 	prescr_procedimento a
	where   Obter_se_exibe_proced(a.nr_prescricao,a.nr_sequencia,a.ie_tipo_proced,'AT') = 'S'
	and	a.nr_prescricao	= nr_prescricao_p
	and not exists (SELECT	1
			from	san_solic_aferese_terap b
			where	b.nr_prescricao = a.nr_prescricao
			and	b.nr_seq_prescr = a.nr_sequencia)
	order by nr_sequencia;

BEGIN

if (nr_prescricao_p IS NOT NULL AND nr_prescricao_p::text <> '') then
	open C01;
	loop
	fetch C01 into
		nr_seq_prescr_w;
	EXIT WHEN NOT FOUND; /* apply on C01 */
		begin
		CALL atualizar_solic_aferese_terap(nr_prescricao_p,	nr_seq_prescr_w, cd_estabelecimento_p, nm_usuario_p);
		end;
	end loop;
	close C01;
end if;



commit;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
