CREATE OR REPLACE PROCEDURE consiste_agendamento_duplic ( dt_agenda_p timestamp, cd_pessoa_fisica_p text, nr_seq_proc_interno_p text, ds_erro_p INOUT text) AS $body$
DECLARE

ie_agendado_w	varchar(1);
ds_erro_w	varchar(255) := '';


BEGIN 
 
select	CASE WHEN count(*)=0 THEN 'N'  ELSE 'S' END  
into STRICT 	ie_agendado_w 
from	agenda_paciente a, 
	agenda b 
where  a.cd_agenda = b.cd_agenda 
and	b.cd_tipo_agenda = 2 
and	a.ie_status_agenda not in ('C','L','B') 
and	trunc(a.dt_agenda)	= dt_agenda_p 
and	a.cd_pessoa_fisica 	= cd_pessoa_fisica_p 
and	a.nr_seq_proc_interno 	= nr_seq_proc_interno_p;
 
if (ie_agendado_w = 'S') then 
	ds_erro_w := WHEB_MENSAGEM_PCK.get_texto(277295,null);
end if;
 
ds_erro_p := ds_erro_w;
 
commit;
 
end;
