CREATE OR REPLACE PROCEDURE consiste_agrupamento_onc ( nr_seq_paciente_p bigint, nr_seq_item_p bigint, ie_tipo_p text, nr_agrupamento_p bigint, nm_usuario_p text, ds_retorno_p INOUT text) AS $body$
DECLARE


qt_reg_w		bigint;
sql_w           varchar(200);
ds_retorno_w    varchar(4000);

BEGIN
ds_retorno_p	:= null;
if (ie_tipo_p	= 'SOL') then
	select	count(*)
	into STRICT	qt_reg_w
	from	paciente_protocolo_soluc
	where	nr_seq_paciente	= nr_seq_paciente_p
	and		nr_seq_solucao	<> nr_seq_item_p
	and		nr_agrupamento	= nr_agrupamento_p;

  BEGIN
        sql_w := 'CALL obter_desc_cons_agrup_onc_md(:1, :2, :3) INTO :ds_retorno_w';

        EXECUTE sql_w
            USING IN qt_reg_w, IN ds_retorno_p, IN 1, OUT ds_retorno_w;

  EXCEPTION
        WHEN OTHERS THEN
            ds_retorno_w := NULL;
  END;
  ds_retorno_p	:= ds_retorno_w;


	select	count(*)
	into STRICT	qt_reg_w
	from	paciente_protocolo_medic
	where	nr_seq_paciente	= nr_seq_paciente_p
	and		coalesce(nr_seq_diluicao::text, '') = ''
	and		coalesce(nr_seq_solucao::text, '') = ''
	and		coalesce(nr_seq_procedimento::text, '') = ''
	and		nr_agrupamento	= nr_agrupamento_p;
BEGIN
        sql_w := 'CALL obter_desc_cons_agrup_onc_md(:1, :2, :3) INTO :ds_retorno_w';

        EXECUTE sql_w
            USING IN qt_reg_w, IN ds_retorno_p, IN 2, OUT ds_retorno_w;

    EXCEPTION
        WHEN OTHERS THEN
            ds_retorno_w := NULL;
    END;
	ds_retorno_p	:= ds_retorno_w;
elsif (ie_tipo_p	= 'MED') then

	select	count(*)
	into STRICT	qt_reg_w
	from	paciente_protocolo_soluc
	where	nr_seq_paciente	= nr_seq_paciente_p
	and		nr_agrupamento	= nr_agrupamento_p;
BEGIN
        sql_w := 'CALL obter_desc_cons_agrup_onc_md(:1, :2, :3) INTO :ds_retorno_w';

        EXECUTE sql_w
            USING IN qt_reg_w, IN ds_retorno_p, IN 3, OUT ds_retorno_w;

    EXCEPTION
        WHEN OTHERS THEN
            ds_retorno_w := NULL;
    END;
	ds_retorno_p	:= ds_retorno_w;

	select	count(*)
	into STRICT	qt_reg_w
	from	paciente_protocolo_medic
	where	nr_seq_paciente	= nr_seq_paciente_p
	and		coalesce(nr_seq_diluicao::text, '') = ''
	and		coalesce(nr_seq_solucao::text, '') = ''
	and		coalesce(nr_seq_procedimento::text, '') = ''
	and		nr_seq_material	<> nr_seq_item_p
	and		nr_agrupamento	= nr_agrupamento_p;
BEGIN
        sql_w := 'CALL obter_desc_cons_agrup_onc_md(:1, :2, :3) INTO :ds_retorno_w';

        EXECUTE sql_w
            USING IN qt_reg_w, IN ds_retorno_p, IN 4, OUT ds_retorno_w;

    EXCEPTION
        WHEN OTHERS THEN
            ds_retorno_w := NULL;
    END;
	ds_retorno_p	:= ds_retorno_w;
end if;

end;
$body$
