ie_consiste_alta_w	varchar(1);
ie_alta_w		varchar(1);
nr_atendimento_w	ap_lote.nr_atendimento%type;
	

BEGIN

select 	max(nr_atendimento),
		max(CASE WHEN coalesce(Obter_data_alta_Atend(nr_atendimento)::text, '') = '' THEN 'N'  ELSE 'S' END )
into STRICT	nr_atendimento_w,
		ie_alta_w
from 	ap_lote
where 	nr_sequencia = nr_seq_lote_p;

