	select 	max(CASE WHEN coalesce(Obter_data_alta_Atend(nr_atendimento_p)::text, '') = '' THEN 'N'  ELSE 'S' END )
	into STRICT	ie_alta_w
	;
end if;
if (ie_alta_w = 'S') then
	CALL wheb_mensagem_pck.exibir_mensagem_abort(170906);
end if;

end;
