SET client_encoding TO 'UTF8';





CREATE OR REPLACE PROCEDURE consiste_alt_venc_tit_camara (nr_titulo_p bigint) AS $body$
DECLARE


ie_processo_camara_w			pls_parametros_camara.ie_processo_camara%type;
nr_titulo_pagar_w				pls_titulo_lote_camara.nr_titulo_pagar%type;
nr_seq_lote_w					pls_titulo_lote_camara.nr_sequencia%type;
ie_baixa_camara_compensacao_w	bigint;
cd_estabelecimento_w			integer;


BEGIN

select	max(cd_estabelecimento)
into STRICT	cd_estabelecimento_w
from	titulo_pagar
where	nr_titulo	= nr_titulo_p;

if (cd_estabelecimento_w IS NOT NULL AND cd_estabelecimento_w::text <> '') then

	/*verificar se o título que esta recebendo a baixa está em um lote de camra de compensação que não está baixado.*/

	select	count(a.nr_titulo_pagar),
			max(b.nr_sequencia)
	into STRICT	nr_titulo_pagar_w,
			nr_seq_lote_w
	from	pls_titulo_lote_camara a,
			pls_lote_camara_comp b
	where	a.nr_seq_lote_camara	= b.nr_sequencia
	and		a.nr_titulo_pagar		= nr_titulo_p;

	select	max(a.ie_processo_camara)
	into STRICT	ie_processo_camara_w
	from	pls_parametros_camara a
	where 	cd_estabelecimento = cd_estabelecimento_w;

	if (coalesce(ie_processo_camara_w,'CO') = 'CA') and (nr_titulo_pagar_w > 0) then /*se  o processo câmara de compensação for regime de caixa e o titulo estiver na camara*/
			CALL wheb_mensagem_pck.exibir_mensagem_abort(335816,	'NR_SEQ_LOTE_W=' || nr_seq_lote_w ||
									';NR_TITULO_W=' || nr_titulo_p);
	end if;
end if;

end;
$body$
LANGUAGE PLPGSQL
SECURITY DEFINER
;
-- REVOKE ALL ON PROCEDURE consiste_alt_venc_tit_camara (nr_titulo_p bigint) FROM PUBLIC;

